/*
   generates links between all linked nodes and
   returns a link object with link properties.
*/

function generateLinks(data) {

  /*
    expand to standalone paths? bezier curve: https://svg-path-visualizer.netlify.app/bezier-curve/
    - start: can specify point or enter coordinate values directly (run thru xScale)
    - control: ???
    - end: same as start
    output: M startX,startY C controlX1,controlY1 controlX2,controlY2 endX, endY
  */

  const links = []; //array of all node links

  data.forEach((item, i) => {
    if (!item.target) {
      //if the node does not have a target, skip generating link
      return
    } else {
      item.target.forEach((n) => {
        console.log(n);
      });
    }
  });
  return links;
};

export default generateLinks;
