import React, { useState, useEffect, useRef } from 'react';
import { select, scaleLinear, axisBottom, line, curveCardinal, drag } from "d3";
import ResizeObserver from "resize-observer-polyfill"; //otherwise breaks in safari and edge
import "./App.css";
import Data from "./data.json";

//defines dom element for resize observer to watch
const useResizeObserver = ref => {
  const [dimensions, setDimensions] = useState(null);
  //only access reference (aka the svg) when it's rendered
  useEffect(() => {
    const observeTarget = ref.current;
    //create new resize observer w callback that takes resize target as args
    const resizeObserver =  new ResizeObserver((entries) => {
      //contentRect is the part ResizeObserver returns that has xy dimensions
      entries.forEach(entry => {
        setDimensions(entry.contentRect);
      });
    });
    resizeObserver.observe(observeTarget);
    //cleanup function when unmount
    return () => {
      resizeObserver.unobserve(observeTarget);
    };
  }, []);
  return dimensions;
}

function App() {

  //data is initially set to imported json array Data
  const [data, setData] = useState(Data);

  const radius = 6;
  //reference object to pass to svg in React Dom
  const svgRef = useRef();
  //svg wrapped in div bc otherwise resize observer callback won't work
  const wrapperRef = useRef();
  const dimensions = useResizeObserver(wrapperRef);

  //all d3 shennanigans goes in this hook. called initially and on every data change
  useEffect(() => {
    const svg = select(svgRef.current); //"svg" is the current version of svgRef data
    //prevents crashing when useResizeObserver returns null before svg is rendered
    if (!dimensions) return;

    //---scales---//
    //scaleLinear takes domain (data values) & maps onto range (pixel values)
    const xScale = scaleLinear()
      .domain([0, 60]) //todo max(data.x)
      .range([0, dimensions.width]);
    const yScale = scaleLinear()
      .domain([0, 60]) //todo
      .range([0, dimensions.height]);
    //inverse scales for drag behavior
    const xScaleInverse = scaleLinear()
      .domain([-dimensions.width, dimensions.width])
      .range([-60, 60]);//todo
    const yScaleInverse = scaleLinear()
      .domain([-dimensions.height, dimensions.height])
      .range([-60, 60]);//todo

    //---draw x axis---//
    const xAxis = axisBottom(xScale);

    svg
      .select(".x-axis")
      .style("transform", `translateY(${dimensions.height}px)`)
      .call(xAxis); //replace class "x-axis" w xAxis

    //--drag behavior--//
    const dragged = drag()
      .on("drag", e => {
        const newData = [...data]; //always make a copy when updating array
        //find array element in newData (id = index) and set new x y coordinates
        //define id as array.length-1
        newData[e.subject.id].x += xScaleInverse(e.dx);
        newData[e.subject.id].y += yScaleInverse(e.dy);
        // console.log(newData[e.subject.id].x + ", " + newData[e.subject.id].y);
        setData(newData);
      });

    //---draw points---//
    svg
      .selectAll("circle")
      .data(data)
      .join("circle")
      .attr("r", radius)
      .attr("cx", d => xScale(d.x))
      .attr("cy", d => yScale(d.y))
      .call(dragged);

    //---draw lines---//
    const myLine = line()
      .x(d => xScale(d.x))
      .y(d => yScale(d.y))
      .curve(curveCardinal);

    svg
      .selectAll(".line") //if no line class, it selects x axis too
      .data([data]) //just draw 1 line for the whole array
      .join("path")
      .attr("class", "line")
      .attr("d", value => myLine(value))
      .attr("fill", "none")
      .attr("stroke", "black");

    //---draw labels---//
    svg
    .selectAll(".label")
    .data(data)
    .join("text")
    .attr("class", "label")
    .text(node => node.name)
    .attr("x", node => xScale(node.x))
    .attr("y", node => yScale(node.y))
    .attr("text-anchor", "middle")
    .attr("font-size", 24);

  }, [data, dimensions]);

  return (
    <div ref={wrapperRef}>
      <svg ref={svgRef}>
        <g className="x-axis" />
      </svg>
    </div>
  );
}

export default App;
