import React from 'react';
// import updateSrc from './updateSrc';
/*
   UI and function for adding new points.
*/

class NewInstantForm extends React.Component {
  //controlled component, meaning React is the "single source of truth"
  constructor(props) {
    super(props);
    this.state = {
      label: '',
      xValue: props.target.x, //"target" is scaled mouse click coordinates. Graphics.js addInstant
      yValue: props.target.y,
      scale: props.activeScale,
      color: '',
      opacity: '',
      radius: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(e) {
    const value = e.target.value;
    const name = e.target.name;
    this.setState({
      [name]: value
    });
  }

  handleClose(e) {
    this.props.setToggle(null);
    e.preventDefault();
  }

  handleSubmit(e) { //consider turning into useCallback
    e.preventDefault();
    const newData = {...this.props.data};
    const lastKey = Object.keys(newData).pop() ? parseInt(Object.keys(newData).pop()) : -1;
    const lastInstant = newData[lastKey]; //last instant of data

    const tempx = parseFloat(e.target.xValue.value);
    //if y pos specified, convert to float. otherwise, return null
    const tempy = e.target.yValue.value ? parseFloat(e.target.yValue.value) : null;

    //error handling. btw, isNan(null) === false
    if (isNaN(tempx) && isNaN(tempy)) {
      alert("Make sure Time and y Position are numbers!");
    } else if (isNaN(tempx)) {
      alert("Make sure Time is a number!");
    } else if (isNaN(tempy)) {
      alert("Make sure y Position is a number!");
    } else {
      //generate instant
      newData[`${lastKey+1}`] = {
        "x":tempx,
        "y": e.target.yValue.value ? tempy : ( lastInstant ? lastInstant.y : 0 ), // if y not specified, take value of last instant
        "scale": e.target.scale.value,
        "target": null,
        "name": e.target.label.value,
        ...(e.target.color && {"color": e.target.color.value}), //if color is specified, create color entry in data
        ...(e.target.radius && {"radius": e.target.radius.value}),
        ...(e.target.opacity && {"opacity": e.target.opacity.value})
      };
    };

    //order of parameters is important
    props.updateSrc(newData);
    // updateSrc(newData, this.props.data, this.props.setData, this.props.setHistory, this.props.history,  this.props.setToggle);

  }

  render() {
    const scalesList = Object.keys(this.props.scales).map((key, i) => {
      return <option value={key} key={i}>{key}</option>
    });

    return (
      <form className="ui absolute center" onSubmit={this.handleSubmit}>
        <button className="right" onClick={this.handleClose}>x</button><br/>
        <label>
          Label
          <input type="text"
            name="label"
            placeholder="any unicode characters"
            value={this.state.label}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Time<span className="redText">* </span>
          <input
            type="number"
            name="xValue"
            placeholder="numbers only"
            value={this.state.xValue}
            onChange={this.handleChange}
            required
          />
        </label><br/>
        <label>
          y Position
          <input
            type="number"
            name="yValue"
            placeholder="#s only (default 0)"
            value={this.state.yValue}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Scale<span className="redText">* </span>
          <select name="scale" value={this.state.scale} onChange={this.handleChange}>
            {scalesList}
          </select>
        </label><br/>
        <label>
          Color
          <input
            type="text"
            name="color"
            placeholder="blue, #4DA6FF, etc."
            value={this.state.color}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Opacity
          <input
            type="number"
            name="opacity"
            placeholder="1"
            value={this.state.opacity}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Radius
          <input
            type="number"
            name="radius"
            placeholder="5"
            value={this.state.radius}
            onChange={this.handleChange}
          />
        </label><br/>
        <br/>
        <input type="submit" value="Submit"/>
      </form>
    );
  }
};

export default NewInstantForm;
