import React, { useState } from 'react';

/*
   UI and function for adding new zones.
*/

function NewScaleForm({zones, setZones, setToggle, scales, activeScale}) {
  const [name, setName] = useState("");
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  const [select, setSelect] = useState(activeScale);
  //go thru list of scales in ChronoJSON and create a selection entry for each
  const scalesList = Object.keys(scales).map((key, i) => {
    return <option value={key} key={i}>{key}</option>
  });
  //optional parameters
  const [yStart, setYStart] = useState("");
  const [yEnd, setYEnd] = useState("");
  const [color, setColor] = useState("");
  const [opacity, setOpacity] = useState("");

  const handleSubmit = (e) => {
    //generate new zone
    const zone = {
      "id": zones.length ? zones[zones.length-1].id + 1 : 0,
      "name": label,
      "start": parseFloat(start), //cases where start > end?
      "end": parseFloat(end),
      "scale": select,
      ...(yStart && {"yStart": yStart}),
      ...(yEnd && {"yEnd": yEnd}),
      ...(color && {"color": color}),
      ...(opacity && {"opacity": opacity})
    }
    const newZones = [...zones, zone];
    setZones(newZones);
    setToggle(null);
    e.preventDefault();
  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <label>
        Name<span className="red">* </span>
        <input type="text"
          placeholder="any unicode characters"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Minimum Value<span className="red">* </span>
        <input type="text"
          placeholder="#s for now"
          value={min}
          onChange={(e) => setMin(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Maximum Value<span className="red">* </span>
        <input type="text"
          placeholder="#s for now"
          value={max}
          onChange={(e) => setMax(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Units<span className="red">* </span>
        <input type="text"
          placeholder="ie: Unicorns per 100K"
          value={units}
          onChange={(e) => setUnits(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Vertical Position<span className="red">* </span>
        <input
          type="text"
          placeholder="#s, dates, logical operators"
          value={yPos}
          onChange={(e) => setYPos(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Segments
        <input
          type="number"
          placeholder="granularities within scale"
          value={segmentsNum}
          onChange={(e) => setSegmentNum(e.target.value)}
        />
      </label><br/>
      <input type="submit" value="Submit"/>
    </form>
  )
};

export default NewScaleForm;
