//iterates thru xy values in json data to find min and max values
function findMinMax(data) {
  var [minX, minY] = [Infinity, Infinity]; //assign values for multiple vars
  var [maxX, maxY] = [-Infinity, -Infinity];
  var tmpX, tmpY;

  for (var i = data.length-1; i>=0; i--) {
    tmpX = data[i].x;
    tmpY = data[i].y;
    if (tmpX < minX) minX = tmpX;
    if (tmpX > maxX) maxX = tmpX;
    if (tmpY < minY) minY = tmpY;
    if (tmpY > maxY) maxY = tmpY;
  }
  return(
    [minX, maxX, minY, maxY]
  );
}

/*
   process raw data in function that extract following arrays:
   0: of all nodes excluding loose points
   1: from 1, all links between nodes, {source, target, color}

   Graphic component will be passed these for use in this way:
   - [1] for drawing linkHorizontal
   - Data.nodes for drawing cirlces and labels
*/
function generateLinks(data) {
  // const nodes = []; //array of all nodes that are NOT detached
  const links = []; //array of all node links

  /// IMPORTANT: all ending nodes must be marked w target:null ///

  data.forEach((item, i) => {
    if (item.target !== null) { //detached points are defined w target: null as opposed to being undefined
      // nodes.push(item);
      if (item.target) { //for nodes w multiple targets, create link for each target
        item.target.forEach((n) => {
          links.push({source:item, target:data[n], color:item.lineColor});
        });
      } else {
        links.push({source:item, target:data[i+1], color:item.lineColor})
      };
    }
  });

  return links;
};

function App() {

  //user defined
  const radius = 5;
  const fontSize = 20;

  //initially set to imported ChronoJSON
  var [src, setSrc] = useState(Data);

  /*
  TODO:
  - make a copy of imported data
  - put generateLinks() and findMinMax into a useEffect hook
  */
  const data = {nodes: src.nodes, links:generateLinks(src.nodes), imgs:src.imgs};

  return (
    <>
      <Graphic src={data} minMax = {findMinMax(src.nodes)} radius={radius} fontSize={fontSize}/>
      <NewPointForm nodes={src.nodes}/>
    </>
  );
}

export default App;
