import React, { useState } from 'react';

/*
  For saving and downloading JSON data onto computer.
*/

function ImportDataForm({setToggle}) {
  const [selectedFile, setSelectedFile] = useState();
	const [isSelected, setIsSelected] = useState(false);

	const handleChange = (e) => {
		setSelectedFile(e.target.files[0]);
		setIsSelected(true);
    e.preventDefault();
	};

	const handleSubmit = (e) => {
    e.preventDefault();

    // file is encoded and sent out over HTTP with Content-Type: multipart/form-data
    // const formData = new FormData();
		// formData.append('File', selectedFile);

    //todo
    fetch(
      "/api",
      {
        method: 'POST',
        body: new FormData(selectedFile),
      }
    )
      .then((res) => res.json())
      .then((result) => {console.log("success:", result)})
      .catch((error) => {console.log("error:", error)});

	};

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form
      className="ui absolute center"
      action="/"
      method="POST"
      encType="multipart/form-data"
    >
      <button className="right" onClick={handleClose}>x</button><br/>
      <input type="file" name="data" onChange={handleChange} /><br/>
			{isSelected ? (
				<p>
					File name: {selectedFile.name}<br/>
					File type: {selectedFile.type}<br/>
					Size: {selectedFile.size/1000} KB
				</p>
			) : (
				<p>Select a file to show details</p>
			)}
			<input type="submit" value="Upload"/>
    </form>
  );
}

export default ImportDataForm;
