const InputFormScale = ({scales, label, name, value, onChange}) => {
  const xList = Object.keys(scales.x).map((key, i) => {
    return <option value={key} key={i}>{key}</option>
  });
  const yList = Object.keys(scales.y).map((key, i) => {
    return <option value={key} key={i}>{key}</option>
  });

  return (
    <>
      <label>
        X Scale<span className="redText">* </span>
        <select name="xScale" value={value} onChange={onChange}>
          {xList}
        </select>
      </label><br/>
      <label>
        Y Scale<span className="redText">* </span>
        <select name="yScale" value={yScale} onChange={onChange}>
          {yList}
        </select>
      </label><br/>
    </>
  )
};

export default InputFormScale;
