import React, { useState } from 'react';

/*
   UI and function for adding new zones.
*/

function NewScaleForm({scales, setScales, setToggle, setActiveScale}) {
  const [name, setName] = useState("");
  const [min, setMin] = useState("");
  const [max, setMax] = useState("");
  const [units, setUnits] = useState("");
  const [yPos, setYPos] = useState();
  //optional parameters
  const [segmentsNum, setSegmentNum] = useState([]);

  const handleSubmit = (e) => {
    const newScales = {...scales};

    const tempMin = parseFloat(min);
    const tempMax = parseFloat(max);
    const tempYPos = parseFloat(yPos);

    if (tempYPos>1) {
      alert("Vertical Position must be between 0 and 1");
      return;
    };

    //generate new scale
    newScales[name] = {
      "min": tempMin,
      "max": tempMax,
      "units": units,
      "yPos": tempYPos,
      "segments": [] //todo
    }
    setScales(newScales);
    setActiveScale(name);
    setToggle(null);
    e.preventDefault();
  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right uiButton closeButton" onClick={handleClose}>x</button><br/>
      <label>
        Name<span className="redText">* </span>
        <input type="text"
          placeholder="any unicode characters"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Minimum Value<span className="redText">* </span>
        <input type="text"
          placeholder="#s for now"
          value={min}
          onChange={(e) => setMin(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Maximum Value<span className="redText">* </span>
        <input type="text"
          placeholder="#s for now"
          value={max}
          onChange={(e) => setMax(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Units Label<span className="redText">* </span>
        <input type="text"
          placeholder="ie: Years"
          value={units}
          onChange={(e) => setUnits(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Vertical Position<span className="redText">* </span>
        <input
          type="text"
          placeholder="0 (top) - 1 (bottom)"
          value={yPos}
          onChange={(e) => setYPos(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Segments
        <input
          type="number"
          placeholder="granularities within scale"
          value={segmentsNum}
          onChange={(e) => setSegmentNum(e.target.value)}
        />
      </label><br/>
      <input className="uiButton" type="submit" value="Submit"/>
    </form>
  )
};

export default NewScaleForm;
