import React, { useState } from 'react';
import './stylesheets/index.scss';
import Data from "./data.json";
import Graphic from "./components/Graphic";
import Image from "./components/Image";
import NewPointForm from "./components/NewPointForm";
import InfoBox from "./components/InfoBox";
import ExportDataForm from "./components/ExportDataForm";
// import findMinMax from "./components/findMinMax";



function ToolsBar(props) {
  const [content, setContent] = useState(null);

  const handleClick = (e) => {
    if (content) {
      setContent(null);
    } else {
      switch (e.target.id) {
        case 'addTemporalObject':
          setContent(
            <ul>
              <li><button id="addPoint" onClick={handleClick}>Point</button></li>
              <li><button>Line</button></li>
              <li><button>Interval</button></li>
              <li><button>Scale</button></li>
            </ul>
          );
        break
        case 'addInflections':
          setContent(
            <ul>
              <li>Semantic</li>
              <li><button>Cancelled</button></li>
              <li><button>Weight</button></li>
              <li><button>Foreshadowing</button></li>
              <li>Syntactic</li>
              <li><button>Causality</button></li>
              <li><button>Acceleration</button></li>
            </ul>
          );
        break
        case 'toggle':
          setContent(
            <ul>
              <li>Graphics</li>
              <li><button>Scrubber</button></li>
              <li><button>Scale Format</button></li>
              <li><button id="layers" onClick={handleClick}>Layers</button></li>
              <li>UI</li>
              <li><button>Legend</button></li>
            </ul>
          );
        break
        case 'showLabel':
          props.showLabel ?
            props.setShowLabel(false)
          : props.setShowLabel(true);
        break
        case 'file':
          setContent(
            <ul>
              <li><button id="import" onClick={handleClick}>Import</button></li>
              <li><button id="save" onClick={handleClick}>Save & Export</button></li>
            </ul>
          );
        break
        default:
          setContent(null);
          props.setToggle(e.target.id);
      }
    };
    e.preventDefault();
  }

  return (
    <>
      <nav className="ui container flexRow flexSpace">
        <div className="halfWidth">
          <ul className="inline">
            <li><button id="addTemporalObject" onClick={handleClick}>Add Temporal Object</button></li>
            <li><button id="addInflections" onClick={handleClick}>Add Inflections</button></li>
            <li><button id="showLabel" onClick={handleClick}>{props.showLabel ? "Show IDs" : "Show Labels"}</button></li>
            <li><button id="toggle" onClick={handleClick}>Toggle</button></li>
            <li><button id="undo" onClick={handleClick}>Undo</button></li>
          </ul>
        </div>
        <div className="halfWidth right">
          <ul className="inline">
            <li><button id="file" onClick={handleClick}>File</button></li>
            <li><button id="settings" onClick={handleClick}>Settings</button></li>
          </ul>
        </div>
      </nav>
      {content && <div className="ui absolute dropdown" style={{left: 0 + '%'}}>
        {content}
      </div>}
    </>
  );
}


function App() {

  //blank ChronoJSON template
  const defaultJSON = {
    "imgs": [],
    "nodes":
    [
      []
    ],
    "intervals":
    []
  };

  //user defined
  const radius = 5;

  //for the entire ChronoJSON
  const [src, setSrc] = useState(Data);

  //for ChronoJSON history
  const [history, setHistory] = useState([])

  //for toggling the active data as used by scrubber
  const [activeData, setActiveData] = useState(0)
  //for toggling the active layer
  const [activeLayer, setActiveLayer] = useState(0);

  //pertaining to datapoints only
  const [data, setData] = useState(src[activeData].nodes); //todo: comparative timelines, layering
  //pertaining to intervals only
  const [intervals, setIntervals] = useState(src.intervals);
  //toggles datapoint IDs vs labels
  const [showLabel, setShowLabel] = useState(true);

  //UI window toggles
  const [toggle, setToggle] = useState();
  //UI info boxes for points, lines, etc.
  const [info, setInfo] = useState();


  /*
    TODO:
    - findMinMax() adjust less frequently. ie: if new data can be panned to, no need to adjust viewport
    - for piecewise timeline, make function that generates different graphics
    - create a master array of all snapshots of data (for scrubber)
  */

  return (
    <>
      <ToolsBar setToggle={setToggle} data={data} showLabel={showLabel} setShowLabel={setShowLabel}/>
      {toggle==="addPoint" ? <NewPointForm data={data} setData={setData} setToggle={setToggle}/> : null}
      {toggle==="save" ? <ExportDataForm src={src} data={data} activeData={activeData} setToggle={setToggle}/> : null}
      <div id="main" className="container fullHeight">
        {data.length ?
          <Graphic
            data={data}
            setData={setData}
            intervals={intervals}
            setIntervals={setIntervals}
            radius={radius}
            setInfo={setInfo}
            showLabel={showLabel}
          />
          : <div className="flexCenter">Upload data or <button onClick={() => setToggle("addPoint")}>Add new point</button></div>
        }
        {src.imgs.length ? <Image urls={src.imgs}/> : null}
      </div>
      {info ? <InfoBox info={info} setInfo={setInfo} data={data} setData={setData}/> : null}
    </>
  );
}

export default App;
