/*
  function for updating source JSON
*/

function updateSrc({newData, setSrc, data, history, setHistory, setToggle}) {
  props.setHistory([...props.history, props.data]);
  props.setSrc(props.newData);
  props.setToggle(null);
};

export default updateSrc;
