import deleteInstant from "./deleteInstant";
import EditForm1 from "./EditForm1";
import EditForm from "./EditForm";
import React, { useState } from 'react';

/*
  - pass object info and mouseclick position to component
  - component is a div set at mouseclick position and displays object info as text
*/

function InfoBox({info, setInfo, history, setHistory, data, setData, src, setSrc}) {
  //init
  const target = info.target;
  var content = null;

  //states
  const [editMode, setEditMode] = useState(false);

  //values directly from data, immutable by onChange
  const dataValues = {
    sourceX: target.source?target.source[1].x:"",
    sourceY: target.source?target.source[1].y:"",
    targetX: target.target?target.target[1].x:"",
    targetY: target.target?target.target[1].y:"",
    text: target.text?target.text:"",
    color: target.color?target.color:"",
    opacity: target.opacity?target.opacity:"",
    width: target.width?target.width:"",
    importance: target.importance?target.importance:""
  };
  //values for input forms, mutable
  const [values, setValues] = useState(dataValues);

  /*
    deleting point i:
    - find data[i-1] and set its target to data[i+1]. then remove data[i] from array
    - if data[i] is first point, just remove from array

    - if first point => just delete
    - prevNode exists but is not linked => just delete
    - prevNode exists and is linked, but no nextNode => set prevNode target to null, then delete
    - prevNode exists, is linked, and has next node => set prevNode target to nextNode, then delete
    - prevNode exists, is linked to multiple nodes => remove target node from prevNode, then delete
  */

  const handleChange = (e) => {
    e.preventDefault();
    const { value, name } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
  }

  const handleCancel = (e) => {
    e.preventDefault();
    //reset all values to json data version
    setValues(dataValues);
    setEditMode(false);
  }

  const handleSubmitInterval = (e) => {
    e.preventDefault();
    const newData = {...data};
    const xyArray = [values.sourceX, values.sourceY, values.targetX, values.targetY];

    //error handling
    const xyNums = xyArray.map ( n => {
        if (isNaN(parseFloat(n))) {
            alert("Please input valid times and/or Y positions!");
            return;
        } else {
          return parseFloat(n);
        }
      });

    const node = newData[target.source[0]];
    node.x = xyNums[0];
    node.y = xyNums[1];
    newData[target.target[0]].x = xyNums[2];
    newData[target.target[0]].y = xyNums[3];
    node.intervalColor = values.color;
    node.intervalWidth = values.width;
    node.intervalOpacity = values.opacity;
    node.intervalText = values.text;
    // console.log(data);
    // console.log(newData);
    setData(newData);
    setEditMode(false);
    setInfo(null);
  }

  const handleDeleteInterval = (e) => {
    e.preventDefault();
    setHistory([...history, data]);
    deleteInstant(target, data, setData);
    setInfo(null);
  }

  const handleDeleteInstant = (e) => {
    e.preventDefault();
    setHistory([...history, data]);
    deleteInstant(target, data, setData);
    setInfo(null);
  }

  switch (info.type) {
    case 'interval':
      content =
        <form className="container">
          <div className="container flexRow">
            <div className="containerLeft textRight textSecondary">
              From: &nbsp;<br/>
              To: &nbsp;<br/>
              Text: &nbsp;<br/>
              Color: &nbsp;<br/>
              Opacity: &nbsp;<br/>
              Width: &nbsp;<br/>
              Importance: &nbsp;<br/>
            </div>
            <div className="containerRight">
              <EditForm name="sourceX" value={values.sourceX} nullValue={"n/a"} inputType="text" placeholder="numbers for now" editMode={editMode} handleChange={handleChange}/>,&nbsp;
              <EditForm name="sourceY" value={values.sourceY} nullValue={"n/a"} inputType="text" placeholder="numbers for now" editMode={editMode} handleChange={handleChange}/><br/>
              <EditForm name="targetX" value={values.targetX} nullValue={"n/a"} inputType="text" placeholder="numbers for now" editMode={editMode} handleChange={handleChange}/>,&nbsp;
              <EditForm name="targetY" value={values.targetY} nullValue={"n/a"} inputType="text" placeholder="numbers for now" editMode={editMode} handleChange={handleChange}/><br/>
              <EditForm name="text" value={values.text} nullValue={"n/a"} inputType="text" placeholder="any unicode character" editMode={editMode} handleChange={handleChange} /><br/>
              <EditForm name="color" value={values.color} nullValue={"black"} inputType="text" placeholder="blue, #4DA6FF, etc." editMode={editMode} handleChange={handleChange} /><br/>
              <EditForm name="opacity" value={values.opacity} nullValue={"1"} inputType="text" placeholder="0(clear) - 1(opaque)" editMode={editMode} handleChange={handleChange} /><br/>
              <EditForm name="width" value={values.width} nullValue={"1.67"} inputType="text" placeholder="any real number" editMode={editMode} handleChange={handleChange} /><br/>
              <EditForm name="importance" value={values.importance} nullValue={"0"} inputType="text" placeholder="any integer" editMode={editMode} handleChange={handleChange} /><br/>
            </div>
          </div>
          <div className="flexCenter">
            {editMode ?
              <><button className="buttonSecondary" onClick={handleSubmitInterval}>Submit</button> &nbsp;
              <button className="buttonSecondary" onClick={handleCancel}>Cancel</button></>
            : <><button className="buttonSecondary">Delete</button> &nbsp;
              <button className="buttonSecondary" onClick={(e) => {e.preventDefault();setEditMode(true)}}>Edit</button>
              </>
            }
          </div>
        </form>
    break
    case 'zone':
      content =
        <>
          ID: {target.id}<br/>
          Label: {target.name}<br/>
          Time Start: {target.start}<br/>
          Time End: {target.end}<br/>
          Y Start: {target.yStart ? target.yStart : "n/a"}<br/>
          Y End: {target.yEnd ? target.yEnd : "n/a"}<br/>
          Color: {target.color ? target.color : "#999 (default)"}<br/>
          Opacity: {target.opacity ? target.opacity : "0.5"}<br/>
        </>
    break
    default:
      content =
        <>
          ID: {target[0]}<br/>
          Time: {target[1].x}<br/>
          Y Position: {target[1].y}<br/>
          Target(s): {target[1].target ? target[1].target : "n/a"}<br/>
          Color: {target[1].color ? target[1].color : "Black"}<br/>
          Opacity: {target[1].opacity ? target[1].opacity : "1"}<br/>
          Radius: {target[1].radius ? target[1].radius : "5 (default)"}<br/>
          <button className="buttonSecondary" onClick={handleDeleteInstant}>Delete Instant</button>
        </>
  };

  return (
    <div className="ui absolute" style={{left: info.position[0] + 'px', top: info.position[1] + 'px'}}>
      {content}
    </div>
  );
}

export default InfoBox;
