import React from 'react';

/*
   UI and function for adding new points.
*/

class NewInstantForm extends React.Component {
  //controlled component, meaning React is the "single source of truth"
  constructor(props) {
    super(props);
    this.state = {
      label: '',
      xValue: props.target.x, //"target" is scaled mouse click coordinates. Graphics.js addInstant
      yValue: props.target.y,
      scale: props.activeScale,
      color: '',
      opacity: '',
      radius: '',
      endPoint: false,
      targets: '',
      lineColor: '',
      lineWidth: '',
      lineOpacity: '',
      lineDashed: '',
      lineColorB: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(e) {
    //if it's a checkbox, evaluate if checked. otherwise, evaluate the value
    const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
    // const value = () => {
    //   switch (e.target.type) {
    //     case 'checkbox':
    //       return e.target.checked
    //       break;
    //     case 'select':
    //
    //       break;
    //     default:
    //       return e.target.value
    //   }
    // }
    const name = e.target.name;
    this.setState({
      [name]: value
    });
  }

  handleClose(e) {
    this.props.setToggle(null);
    e.preventDefault();
  }

  handleSubmit(e) { //consider turning into useCallback
    e.preventDefault();
    const newData = {...this.props.data};

    const tempx = parseFloat(e.target.xValue.value);
    //if y pos specified, convert to float. otherwise, return null
    const tempy = e.target.yValue.value ? parseFloat(e.target.yValue.value) : null;

    //error handling. btw, isNan(null) === false
    if (isNaN(tempx) && isNaN(tempy)) {
      alert("Make sure Time and y Position are numbers!");
    } else if (isNaN(tempx)) {
      alert("Make sure Time is a number!");
    } else if (isNaN(tempy)) {
      alert("Make sure y Position is a number!");
    } else {
      //generate instant
      const lastKey = Object.keys(newData).pop();
      const lastInstant = newData[lastKey]; //last instant of data
      newData[parseFloat(lastKey)+1] = {
        "name": e.target.label.value,
        "x":tempx,
        "y": e.target.yValue.value ? tempy : ( lastInstant ? lastInstant.y : 0 ), // if y not specified, take value of last instant
        "scale": e.target.scale.value,
        "target": this.state.endPoint ? null //if endpoint, set target to null
            : !lastKey ? [1] //if first instant, set target to 1
            : e.target.targets.value.length > 1 ? e.target.targets.value.split(',').map(Number) //if multiple targets specified, turn IDs into array
            : e.target.targets.value ? [parseInt(e.target.targets.value)] //if a single value is specified, set it as target ID
            : [lastKey + 2], //else, set target ID as instant.id+1 (aka lastInstant.id+2)
        ...(e.target.color && {"color": e.target.color.value}), //if color is specified, create color entry in data
        ...(e.target.radius && {"radius": e.target.radius.value}),
        ...(e.target.opacity && {"opacity": e.target.opacity.value})
      };
      // const instant = {
      //   "id": instants.length ? lastInstant.id + 1 : 0, //id = id of last instant + 1. if first instant, id = 0
      //   "name": e.target.label.value,
      //   "x":tempx,
      //   "y": e.target.yValue.value ? tempy : ( lastInstant ? lastInstant.y : 0 ), // if y not specified, take value of last instant
      //   "scale": e.target.scale.value,
      //   "target": this.state.endPoint ? null //if endpoint, set target to null
      //       : !instants.length ? [1] //if first instant, set target to 1
      //       : e.target.targets.value.length > 1 ? e.target.targets.value.split(',').map(Number) //if multiple targets specified, turn IDs into array
      //       : e.target.targets.value ? [parseInt(e.target.targets.value)] //if a single value is specified, set it as target ID
      //       : [lastInstant.id + 2], //else, set target ID as instant.id+1 (aka lastInstant.id+2)
      //   ...(e.target.color && {"color": e.target.color.value}), //if color is specified, create color entry in data
      //   ...(e.target.radius && {"radius": e.target.radius.value}),
      //   ...(e.target.opacity && {"opacity": e.target.opacity.value})
      };
      console.log(newData);
      /*
      const newInstants = () => {
        if (instants.length && instants[instants.length-1].target) {
          const newInstantsLinked = instants.map(n =>
            {
              //if linked, set target of last instant to ID of new instant and set line properties
              if (n === instants[instants.length-1]) {
                return (
                  {
                    ...n,
                    ...(e.target.lineColor && {"lineColor": e.target.lineColor.value}),
                    ...(e.target.lineWidth && {"lineWidth": e.target.lineWidth.value}),
                    ...(e.target.lineOpacity && {"lineOpacity": e.target.lineOpacity.value}),
                    ...(e.target.lineDashed && {"lineDashed": e.target.lineDashed.value})
                  }
                )
              } else { //leave other instants as is
                return n;
              }
            }
          );
          //then, return updated instants plus new instant
          return([...newInstantsLinked, instant]);
        } else {
          //if not linked, return orignal instants with just new instant added
          return ([...instants, instant]);
        }
      };
      // const newSrc = Object.assign({}, this.props.src, {instants: newInstants()});
      this.props.setData(newInstants);

    }

    this.props.setToggle(null);
*/
  }

  render() {
    const scalesList = Object.keys(this.props.scales).map((key, i) => {
      return <option value={key} key={i}>{key}</option>
    });

    return (
      <form className="ui absolute center" onSubmit={this.handleSubmit}>
        <button className="right" onClick={this.handleClose}>x</button><br/>
        <label>
          Label<span className="redText">* </span>
          <input type="text"
            name="label"
            placeholder="any unicode characters"
            value={this.state.label}
            onChange={this.handleChange}
            required
          />
        </label><br/>
        <label>
          Time<span className="redText">* </span>
          <input
            type="number"
            name="xValue"
            placeholder="numbers only"
            value={this.state.xValue}
            onChange={this.handleChange}
            required
          />
        </label><br/>
        <label>
          y Position
          <input
            type="number"
            name="yValue"
            placeholder="#s only (default 0)"
            value={this.state.yValue}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Scale<span className="redText">* </span>
          <select name="scale" value={this.state.scale} onChange={this.handleChange}>
            {scalesList}
          </select>
        </label><br/>
        <label>
          Color
          <input
            type="text"
            name="color"
            placeholder="blue, #4DA6FF, etc."
            value={this.state.color}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Opacity
          <input
            type="number"
            name="opacity"
            placeholder="1"
            value={this.state.opacity}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Radius
          <input
            type="number"
            name="radius"
            placeholder="5"
            value={this.state.radius}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          End point?
          <input
            type="checkbox"
            name="endPoint"
            checked={this.state.endPoint}
            onChange={this.handleChange}
          />
        </label><br/>
        {!this.state.endPoint &&
          <>
            <label>
              Target(s)
              <input
                type="text"
                name="targets"
                placeholder="instant IDs ie: 4, 5"
                value={this.state.targets}
                onChange={this.handleChange}
              />
            </label><br/>
            <label>
              Line Color
              <input
                type="text"
                name="lineColor"
                placeholder="blue, #4DA6FF, etc."
                value={this.state.lineColor}
                onChange={this.handleChange}
              />
            </label><br/>
            <label>
              Line Width
              <input
                type="number"
                name="lineWidth"
                placeholder="1.67"
                value={this.state.lineWidth}
                onChange={this.handleChange}
              />
            </label><br/>
            <label>
              Line Opacity
              <input
                type="number"
                name="lineOpacity"
                placeholder="1"
                value={this.state.lineOpacity}
                onChange={this.handleChange}
              />
            </label><br/>
            <label>
              Line Dashed
              <input
                type="text"
                name="lineDashed"
                placeholder="i.e: 5"
                value={this.state.lineDashed}
                onChange={this.handleChange}
              />
            </label><br/>
            <label>
              Line Color B
              <input
                type="text"
                name="lineColorB"
                placeholder="blue, #4DA6FF, etc."
                value={this.state.lineColorB}
                onChange={this.handleChange}
              />
            </label><br/>
          </>
        }
        <br/>
        <input type="submit" value="Submit"/>
      </form>
    );
  }
};

export default NewInstantForm;
