/*
   generates links between all linked nodes and
   returns a link object with link properties.
*/

function generateLinks(data) {
  const links = []; //array of all node links

  /// IMPORTANT: all ending nodes must be marked w target:null ///

  data.forEach((src, i) => {
    if (src.length) {
      src.forEach((item) => {
        if (!item.target) {
          return
        } else {
          item.target.forEach((n) => {
            links.push({source:item, target:src.find(x => x.id === n), color:item.lineColor ? item.lineColor : item.color ? item.color : null, width:item.lineWidth});
          });
        }
      })
    }
  });
  return links;
};

export default generateLinks;
