import React, { useState } from 'react';
import "./App.css";
import Data from "./data.json";
import Graphic from "./components/Graphic"

function findMinMax(props) {
  var [minX, minY] = [Infinity, Infinity]; //assign values for multiple vars
  var [maxX, maxY] = [-Infinity, -Infinity];
  var tmpX, tmpY;

  for (var i = props.length-1; i>=0; i--) {
    tmpX = props[i].x;
    tmpY = props[i].y;
    if (tmpX < minX) minX = tmpX;
    if (tmpX > maxX) maxX = tmpX;
    if (tmpY < minY) minY = tmpY;
    if (tmpY > maxY) maxY = tmpY;
  }
  return(
    [minX, maxX, minY, maxY]
  );
}

function App() {

  //user defined
  const radius = 5;
  const fontSize = 20;

  //data is initially set to imported json array Data
  var [src, setSrc] = useState(Data.line1);
  // console.log(findMinMax(Data.line1));
  return (
    <>
      <Graphic src={src} minMax = {findMinMax(src)} radius={radius} fontSize={fontSize}/>

    </>
  );
}

export default App;
