import React, { useState } from 'react';
import InputFormScale from './InputFormScale';
import InputFormText from './InputFormText';
import InputFormNum from './InputFormNum';

/*
   UI and function for adding new zones.
*/

function NewZoneForm({zones, updateSrc, setToggle, scales, activeScale}) {
  const [label, setLabel] = useState("");
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  const [select, setSelect] = useState(activeScale);
  //go thru list of scales in ChronoJSON and create a selection entry for each
  const scalesList = Object.keys(scales).map((key, i) => {
    return <option value={key} key={i}>{key}</option>
  });
  //optional parameters
  const [yStart, setYStart] = useState("");
  const [yEnd, setYEnd] = useState("");
  const [color, setColor] = useState("");
  const [opacity, setOpacity] = useState("");

  const handleSubmit = (e) => {
    const newZones = {...zones};
    const lastKey = Object.keys(newZones).pop() ? parseInt(Object.keys(newZones).pop()) : -1;

    //generate new zone
    newZones[`${lastKey+1}`] = {
      "text": label,
      "start": parseFloat(start), //cases where start > end?
      "end": parseFloat(end),
      "scale": select,
      ...(yStart && {"yStart": yStart}),
      ...(yEnd && {"yEnd": yEnd}),
      ...(color && {"color": color}),
      ...(opacity && {"opacity": opacity})
    }

    updateSrc(newZones,"zones");
    e.preventDefault();
  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <label>
        Label<span className="redText">* </span>
        <input type="text"
          placeholder="any unicode characters"
          value={label}
          onChange={(e) => setLabel(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Time Start<span className="redText">* </span>
        <input type="text"
          placeholder="#s, dates, logical operators"
          value={start}
          onChange={(e) => setStart(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Time End<span className="redText">* </span>
        <input type="text"
          placeholder="#s, dates, logical operators"
          value={end}
          onChange={(e) => setEnd(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Scale<span className="redText">* </span>
        <select name="scales" value={select} onChange={(e) => setSelect(e.target.value)}>
          {scalesList}
        </select>
      </label><br/>
      <label>
        Y Start
        <input
          type="text"
          placeholder="#s, dates, logical operators"
          value={yStart}
          onChange={(e) => setYStart(e.target.value)}
        />
      </label><br/>
      <label>
        Y End
        <input
          type="text"
          placeholder="#s, dates, logical operators"
          value={yEnd}
          onChange={(e) => setYEnd(e.target.value)}
        />
      </label><br/>
      <label>
        Color
        <input
          type="text"
          placeholder="blue, #4DA6FF, etc."
          value={color}
          onChange={(e) => setColor(e.target.value)}
        />
      </label><br/>
      <label>
        Opacity
        <input
          type="number"
          placeholder="0 (transparent) - 1 (opaque)"
          value={opacity}
          onChange={(e) => setOpacity(e.target.value)}
        />
      </label><br/>
      <input type="submit" value="Submit"/>
    </form>
  )
};

export default NewZoneForm;
