import React, {useEffect} from 'react';
import HandsFree from 'handsfree';
// import hands from '@mediapipe/hands';

//https://handsfree.js.org

function Hands(props) {
  //init
  const handsFree = new HandsFree({
    hands:{
      enabled: true,
      maxNumHands: 2
    }
  });
  handsFree.enablePlugins('browser');

  useEffect(() => {
    // const handsFree = new HandsFree({
    //   hands:{
    //     enabled: true,
    //     maxNumHands: 2
    //   }
    // });
    // handsFree.enablePlugins('browser');
    console.log(handsFree);
    handsFree.start(()=>{handsFree.pause()}); //initializes loop but pauses it instantly
  }, []);

  //camera on
  // handsFree.on('gotUserMedia', () => console.log("streaming"));
  // handsFree.start(()=>{handsFree.pause()}); //initializes loop but pauses it instantly
  // handsFree.isLooping = props.enabled; //manually stop the loop
  //cleanup/unsubscribe function?

  const handleClick = (e) => {
    e.preventDefault();
    if (!props.enabled){
      props.setEnabled(true);
      // handsFree.start()
      handsFree.unpause();
    } else {
      handsFree.pause();
      // handsFree.stop(); //just refreshes the whole damn thing
      props.setEnabled(false);
    }
  }

  handsFree.on('data', (e) => {
    console.log(e.hands?e.hands.pointer:null);
    // console.log(e.hands);
  });

  /*
    TODO:
    - draw palm Positions
      - shrink for click
      - something for drag (oblong?)
    - add "click" plugin
      - create plugin for "horns" hand gesture
      - on horns, new MouseEvent
    - add "pause" plugin
      - on fist, pause/stop handsFree

  */

  return (
    <button onClick={handleClick}>
      {props.enabled?`disable hands`:`enable hands`}
    </button>
  )
};

export default Hands;
