import React, { useState } from 'react';

function NewIntervalForm({data, setData, setToggle, scales, activeScale, history, setHistory}) {

  const [text, setText] = useState("");
  const [xValue1, setXValue1] = useState("");
  const [yValue1, setYValue1] = useState("");
  const [xValue2, setXValue2] = useState("");
  const [yValue2, setYValue2] = useState("");
  const [select, setSelect] = useState(activeScale);
  const scalesList = Object.keys(scales).map((key, i) => {
    return <option value={key} key={i}>{key}</option>
  });
  //optional parameters
  const [color, setColor] = useState("");
  const [opacity, setOpacity] = useState("");
  const [width, setWidth] = useState("");
  const [dashed, setDashed] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    const newData = {...data};
    const lastKey = Object.keys(newData).pop() ? parseInt(Object.keys(newData).pop()) : -1;
    const lastInstant = newData[lastKey]; //last instant of data

    const tempX1 = parseFloat(xValue1);
    const tempY1 = yValue1 ? parseFloat(yValue1) : null; //if y pos specified, convert to float. otherwise, return null
    const tempX2 = parseFloat(xValue2);
    const tempY2 = yValue2 ? parseFloat(yValue2) : null;

    //error handling. btw, isNan(null) === false
    if (isNaN(tempX1) || isNaN(tempY1) || isNaN(tempX2) || isNaN(tempY2)) {
      alert("Make sure all Times and y Positions are numbers!");
    } else {
      //generate first instant
      newData[`${lastKey+1}`] = {
        "name": "",
        "x":tempX1,
        "y": yValue1 ? tempY1 : ( lastInstant ? lastInstant.y : 0 ), // if y not specified, take value of last instant
        "scale": select,
        "target": [`${lastKey+2}`],
        ...(text && {"intervalText": text}),
        ...(color && {"intervalColor": color}),
        ...(width && {"intervalWidth": width}),
        ...(opacity && {"intervalOpacity": opacity}),
        ...(dashed && {"intervalDashed": dashed})
      };
      //generate second instant
      newData[`${lastKey+2}`] = {
        "name": "",
        "x":tempX2,
        "y": yValue2 ? tempY2 : ( lastInstant ? lastInstant.y : 0 ), // if y not specified, take value of last instant
        "scale": select,
        "target": null
      };
    };

    setHistory([...history, data]);
    setData(newData);
    setToggle(null);
  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <label>
        Label<span className="redText">* </span>
        <input
          type="text"
          placeholder="any unicode character"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
      </label><br/>
      <b>Start:</b><br/>
      <label>
        Time<span className="redText">* </span>
        <input
          type="text"
          placeholder="numbers only"
          value={xValue1}
          onChange={(e) => setXValue1(e.target.value)}
          required
        />
      </label><br/>
      <label>
        y Position
        <input
          type="text"
          placeholder="#s only (default 0)"
          value={yValue1}
          onChange={(e) => setYValue1(e.target.value)}
        />
      </label><br/>
      <b>End:</b><br/>
      <label>
        Time<span className="redText">* </span>
        <input
          type="text"
          placeholder="numbers only"
          value={xValue2}
          onChange={(e) => setXValue2(e.target.value)}
          required
        />
      </label><br/>
      <label>
        y Position
        <input
          type="text"
          placeholder="#s only (default 0)"
          value={yValue2}
          onChange={(e) => setYValue2(e.target.value)}
        />
      </label><br/>
      <b>Properties:</b><br/>
      <label>
        Scale<span className="redText">* </span>
        <select name="scales" value={select} onChange={(e) => setSelect(e.target.value)}>
          {scalesList}
        </select>
      </label><br/>
      <label>
        Color
        <input
          type="text"
          placeholder="blue, #4DA6FF, etc."
          value={color}
          onChange={(e) => setColor(e.target.value)}
        />
      </label><br/>
      <label>
        Width
        <input
          type="number"
          placeholder="1.67"
          value={width}
          onChange={(e) => setWidth(e.target.value)}
        />
      </label><br/>
      <label>
        Certainty
        <input
          type="number"
          placeholder="0-1"
          value={opacity}
          onChange={(e) => setOpacity(e.target.value)}
        />
      </label><br/>
      <label>
        Dashed
        <input
          type="text"
          placeholder="i.e: 5"
          value={dashed}
          onChange={(e) => setDashed(e.target.value)}
        />
      </label><br/><br/>
      <input type="submit" value="Submit"/>
    </form>
  )
}

export default NewIntervalForm;
