import React, { useState } from 'react';
import { ReactComponent as IconFile } from '../icons/file.svg';
import { ReactComponent as IconInstant } from '../icons/instant.svg';
import { ReactComponent as IconMenu } from '../icons/menu.svg';
import { ReactComponent as IconPan } from '../icons/pan.svg';
import { ReactComponent as IconScale } from '../icons/scale.svg';
import { ReactComponent as IconViews } from '../icons/views.svg';
import { ReactComponent as IconCancelled } from '../icons/cancelled.svg';
import { ReactComponent as IconCausation } from '../icons/causation.svg';
//AKA the navbar, holds all the tools, settings, and whatnot on the top of the page

function ToolsBar(props) {
  const [content, setContent] = useState(null);

  const handleClick = (ref, e) => {
    // console.log(ref, e);
    //
    setContent(ref);

    // if (props.toggle && props.toggle===e.target.id) { //if user toggles tool that is already active, deactivate tool
    //   props.setToggle(null);
    //   setContent(null);
    // } else { //otherwise, activate tool
    //   props.setToggle(e.target.id);
    // }

    if (props.toggle && props.toggle===ref) { //if user toggles tool that is already active, deactivate tool
      props.setToggle(null);
      setContent(null);
    } else { //otherwise, activate tool
      props.setToggle(ref);
    }

    e.preventDefault();
  }

  return (
    <>
      <nav className="container flexRow fullWidth containerSpace">
        <div className="container flexRow containerLeft">

          <div className="dropdown left">
            <button onClick={(e) => handleClick("temporalObject", e)} title="Temporal Objects"><IconInstant className="icon" alt="Instant"/></button>
            {content==="temporalObject" && <ul className="dropdown-content">
              <li><button id="addInstant" onClick={handleClick} title="Instant">Instant</button></li>
              <li><button id="addInterval" onClick={handleClick} title="Interval">Interval</button></li>
              <li><button id="addZone" onClick={handleClick} title="Zone">Zone</button></li>
            </ul>}
          </div>
          <div className="dropdown left">
            <button onClick={(e) => handleClick("inflections", e)} title="Inflections"><IconCancelled className="icon" alt="Inflections"/></button>
            {content==="inflections" && <ul className="dropdown-content  ">
              <li className="subheading">Semantic</li>
              <li><button id="cancelled" onClick={handleClick}>Cancelled</button></li>
              <li><button id="importance" onClick={handleClick}>Importance</button></li>
              <li><button id="certainty" onClick={handleClick}>Certainty</button></li>
              <li><button id="foreshadowing" onClick={handleClick}>Foreshadowing</button></li>
              <li className="subheading">Syntactic</li>
              <li><button id="connection" onClick={handleClick}>Connection</button></li>
              <li><button>Causality</button></li>
            </ul>}
          </div>
          <div className="dropdown left">
            <button onClick={(e) => handleClick("scales", e)} title="Scales"><IconScale className="icon" alt="Scale"/></button>
            {content==="scales" && <ul className="dropdown-content  ">
              <li><button id="selectScale" onClick={handleClick} title="Select Active Scale">Select Active Scale</button></li>
              <li><button id="addScale" onClick={handleClick} title="Create Scale">Create Scale</button></li>
              <li><button id="addInterval" onClick={handleClick} title="Expand Scale">Expand Scale</button></li>
            </ul>}
          </div>
          <button className="right" onClick={(e) => handleClick("pan", e)} title="Pan"><IconPan className="icon" alt="Pan"/></button>
        </div>
        <div className="container flexCenter">FileName.json</div>
        <div className="right container flexRow containerRight">
          {props.toggle && <span className="right">Currently active: {props.toggle}</span>}

          <div className="dropdown right">
            <button onClick={(e) => handleClick("views", e)} title="Views"><IconViews className="icon" alt="Views"/></button>
            {content==="views" && <ul className="dropdown-content rightPos">
              <li className="subheading">Graphics</li>
              <li><button id="scrubber" onClick={handleClick}>Scrubber</button></li>
              <li><button>Scale Format</button></li>
              <li><button id="layers" onClick={handleClick}>Layers</button></li>
              <li className="subheading">UI</li>
              <li><button id="showLabel" onClick={handleClick}>{props.showLabel ? "Show IDs" : "Show Labels"}</button></li>
              <li><button id="legend" onClick={handleClick}>Legend</button></li>
            </ul>}
          </div>
          <div className="dropdown right">
            <button className="fullWidth" onClick={(e) => handleClick("file", e)} title="File"><IconFile className="icon" alt="File"/></button>
            {content==="file" && <ul className="dropdown-content  ">
              <li><button id="import" onClick={handleClick}>Import Data</button></li>
              <li><button id="save" onClick={handleClick}>Save & Export</button></li>
              <li><button id="importImg">Import Image</button></li>
            </ul>}
          </div>
          <div className="dropdown right">
            <button onClick={(e) => handleClick("menu", e)} title="Menu"><IconMenu className="icon" alt="Menu"/></button>
            {content==="menu" && <ul className="dropdown-content rightPos">
              <li className="subheading">Edit</li>
              <li><button id="undo">Undo</button></li>
              <li><button id="redo">Redo</button></li>
              <li className="subheading">Settings</li>
              <li><button id="acessibility">Accessibility</button></li>
              <li><button id="theme">Theme</button></li>
              <li className="subheading">NL Chrono</li>
              <li><button id="about">About</button></li>
              <li><button id="documentation">Documentation</button></li>
            </ul>}
          </div>
        </div>
      </nav>

    </>
  );
}

export default ToolsBar;
