import HandsFree from 'handsfree';
import Model from '@mediapipe/hands';

//https://handsfree.js.org/ref/model/hands.html

function Hands(props) {
  const hands = new HandsFree({hands:{maxNumHands: 2}});

  const handleClick = (e) => {
    e.preventDefault();
    if (!props.enabled){
      props.setEnabled(true);
      hands({hands:{enabled:props.enabled}});
      hands.start();
      hands.on('data', ({data}) => {
        console.log(data);
      });
    } else {
      hands.stop();
      props.setEnabled(false);
    }
  }


  return (
    <button onClick={handleClick}>
      {props.enabled?`disable hands`:`enable hands`}
    </button>
  )

};

export default Hands;
