import React, { useState } from 'react';
import './stylesheets/index.scss';
import Data from "./data.json";
import Graphic from "./components/Graphic";
import Image from "./components/Image";
import NewPointForm from "./components/NewPointForm";
import NewIntervalForm from "./components/NewIntervalForm";
import InfoBox from "./components/InfoBox";
import ExportDataForm from "./components/ExportDataForm";
// import ImportDataForm from "./components/ImportDataForm";
import ToolsBar from "./components/ToolsBar";
import Layers from "./components/Layers";
import Scrubber from "./components/Scrubber"


function Legend() {
  return (
    <div className="ui absolute dropdown rightPos">
      Legend
      <ul>
        <li><span style={{backgroundColor: 'black'}}>&#8195;</span> Hank</li>
        <li><span style={{backgroundColor: 'red'}}>&#8195;</span> Peggy</li>
        <li><span style={{backgroundColor: '#4DA6FF'}}>&#8195;</span> Bobby</li>
      </ul>
    </div>
  )
}

function Certainty(props) {
  console.log(props.target);
  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <label>
        Label<span className="red">* </span>
        <input type="text"
          placeholder="any unicode characters"
          value={label}
          onChange={(e) => setLabel(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Time Start<span className="red">* </span>
        <input type="text"
          placeholder="#s, dates, logical operators"
          value={start}
          onChange={(e) => setStart(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Time End<span className="red">* </span>
        <input type="text"
          placeholder="#s, dates, logical operators"
          value={end}
          onChange={(e) => setEnd(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Y Start
        <input
          type="text"
          placeholder="#s, dates, logical operators"
          value={yStart}
          onChange={(e) => setYStart(e.target.value)}
        />
      </label><br/>
      <label>
        Y End
        <input
          type="text"
          placeholder="#s, dates, logical operators"
          value={yEnd}
          onChange={(e) => setYEnd(e.target.value)}
        />
      </label><br/>
      <label>
        Color
        <input
          type="text"
          placeholder="blue, #4DA6FF, etc."
          value={color}
          onChange={(e) => setColor(e.target.value)}
        />
      </label><br/>
      <label>
        Opacity
        <input
          type="number"
          placeholder="0 (transparent) - 1 (opaque)"
          value={opacity}
          onChange={(e) => setOpacity(e.target.value)}
        />
      </label><br/>
      <input type="submit" value="Submit"/>
    </form>
  )
}


function App() {
  //blank ChronoJSON template
  const nullData = {
    "imgs": [],
    "nodes":
    [
      []
    ],
    "intervals":
    []
  };

  //user defined
  const radius = 5;

  //for the entire ChronoJSON
  const [src, setSrc] = useState(Data);

  //for ChronoJSON history
  const [history, setHistory] = useState([])

  //for toggling the active data as used by scrubber
  const [activeData, setActiveData] = useState(0);
  //for toggling the active layer
  const [activeLayer, setActiveLayer] = useState();

  //pertaining to datapoints only
  const [data, setData] = useState(src.nodes[activeData]);
  //pertaining to intervals only
  const [intervals, setIntervals] = useState(src.intervals);
  //pertaining to scales only
  const [scales, setScales] = useState(src.scales);
  //toggles datapoint IDs vs labels
  const [showLabel, setShowLabel] = useState(true);

  //UI window toggles
  const [toggle, setToggle] = useState();
  //UI info boxes for points, lines, etc.
  const [info, setInfo] = useState();
  //Inflection info
  const [inflectTarget, setInflectTarget] = useState();

  /*
    TODO:
    - for piecewise timeline, make function that generates different graphics
  */

  return (
    <>
      <ToolsBar toggle={toggle} setToggle={setToggle} data={data} showLabel={showLabel} setShowLabel={setShowLabel}/>
      {toggle==="scrubber" && <Scrubber activeData={activeData} setActiveData={setActiveData} src={src} setData={setData}/>}
      {toggle==="addPoint" && <NewPointForm data={data} setData={setData} setToggle={setToggle}/>}
      {toggle==="addInterval" && <NewIntervalForm intervals={intervals} setIntervals={setIntervals} setToggle={setToggle}/>}
      {toggle==="save" && <ExportDataForm src={src} data={data} activeData={activeData} setToggle={setToggle}/>}
      {toggle==="legend" && <Legend/>}
      {toggle==="layers" && <Layers activeLayer={activeLayer} setActiveLayer={setActiveLayer} data={data}/>}
      {toggle==="certainty" && <Certainty target={inflectTarget}/>}
      <div id="main" className="container fullHeight">
        {data.length ?
          <Graphic
            data={data}
            setData={setData}
            intervals={intervals}
            setIntervals={setIntervals}
            radius={radius}
            info={info}
            setInfo={setInfo}
            setInflectTarget={setInflectTarget}
            showLabel={showLabel}
            activeLayer={activeLayer}
            toggle={toggle}
            scales={scales}
            setScales={setScales}
          />
          : <div className="flexCenter">Upload data or <button onClick={() => setToggle("addPoint")}>Add new point</button></div>
        }
        {src.imgs.length>0 && <Image urls={src.imgs}/>}
      </div>
      {info && <InfoBox info={info} setInfo={setInfo} data={data} setData={setData}/>}
    </>
  );
}

export default App;
