import React, { useState, useEffect, useRef } from 'react';
import { select, scaleLinear, axisBottom, hierarchy, tree, linkHorizontal, drag, zoom } from "d3";
import useResizeObserver from "./useResizeObserver";
import Image from "./Image";
import Data from "../data.json";

function Graphic({src, minMax, radius, fontSize}) {
  const [data, setData] = useState(src);
  //reference object to pass to svg in React Dom
  const svgRef = useRef();
  //svg wrapped in div bc otherwise resize observer callback won't work
  const wrapperRef = useRef();
  const dimensions = useResizeObserver(wrapperRef);
  const [currentZoomState, setCurrentZoomState] = useState();

  //all d3 shennanigans goes in this hook. called initially and on every data change
  useEffect(() => {
    const svg = select(svgRef.current); //"svg" is the current version of svgRef data
    //prevents crashing when useResizeObserver returns null before svg is rendered
    if (!dimensions) return;
    //prevents crashing before min and max are calculated
    if (!minMax) return;

    const root = hierarchy(data);
    // console.log(root.descendants()); //pass this to find min max. for each: .data
    // console.log(root.links());

    //---scales---//
    //scaleLinear takes domain (data values) & maps onto range (pixel values)
    const xScale = scaleLinear()
      .domain([0, 60]) //0 is minX, 1 is maxX (reference findMinMax)
      .range([0, dimensions.width]);
    const yScale = scaleLinear()
      .domain([0, 60]) //todo
      .range([0, dimensions.height]);
    //inverse scales for drag behavior
    const xScaleInverse = scaleLinear()
      .domain([-dimensions.width, dimensions.width])
      .range([-60, 60]);
    const yScaleInverse = scaleLinear()
      .domain([-dimensions.height, dimensions.height])
      .range([-60, 60]);
    //
    // if (currentZoomState) {
    //   const newXScale = currentZoomState.rescaleX(xScale); //zoom state comes w methods for transformations
    //   xScale.domain(newXScale.domain());
    //   xScaleInverse.range([
    //     -(newXScale.domain()[1]-newXScale.domain()[0]),
    //     (newXScale.domain()[1]-newXScale.domain()[0])
    //   ]);
    // };

    //---draw x axis---//
    // const xAxis = axisBottom(xScale);
    //
    // svg
    //   .select(".x-axis")
    //   .style("transform", `translateY(${dimensions.height}px)`)
    //   .call(xAxis); //replace class "x-axis" w xAxis

    //--drag behavior--//
    const dragged = drag()
      .on("drag", e => {
        const newData = root; //always make a copy when updating array
        console.log(newData);
        // //find array element in newData (id = index) and set new x y coordinates
        // //define id as array.length-1
        // newData[e.subject.data.id].x += xScaleInverse(e.dx);
        // newData[e.subject.data.id].y += yScaleInverse(e.dy);
        // // console.log(newData[e.subject.id].x + ", " + newData[e.subject.id].y);
        // setData(newData);
      });


    //---draw lines---//
    const linker = linkHorizontal()
      .x(node => xScale(node.data.x))
      .y(node => yScale(node.data.y));

    svg
      .selectAll(".link")
      .data(root.links())
      .join("path")
      .attr("class", "link")
      .attr("d", linker)
      .attr("fill", "none")
      .attr("stroke", "black");


    //---draw points---//
    svg
      .selectAll(".node")
      .data(root.descendants())
      .join("circle") //for every node, draw a circle
      .attr("class", ".node")
      .attr("r", radius)
      .attr("fill", node => node.data.color ? node.data.color : "black") //if it has a color other than black, use that
      .attr("cx", node => xScale(node.data.x))
      .attr("cy", node => yScale(node.data.y))
      .call(dragged);

    // svg
    //   .selectAll("circle")
    //   .data(data)
    //   .join("circle")
    //   .attr("r", radius)
    //   .attr("cx", d => xScale(d.x))
    //   .attr("cy", d => yScale(d.y))
    //   .call(dragged);



    //---draw labels---//
    svg
    .selectAll(".label")
    .data(root.descendants())
    .join("text")
    .attr("class", "label")
    .text(node => node.data.name)
    .attr("x", node => xScale(node.data.x))
    .attr("y", node => yScale(node.data.y + 2))
    .attr("text-anchor", "middle")
    .attr("font-size", fontSize);

    //---zoom---//
    // const zoomBehavior = zoom()
    //   .scaleExtent([0.25, 5]) //can zoom 2x smaller and 5x bigger
    //   .translateExtent([[-100, 0], [dimensions.width+100, dimensions.height]]) //limits how far graph can pan
    //   .on("zoom", e => {
    //     const zoomState = e.transform; //returns zoom transformations as an object
    //     setCurrentZoomState(zoomState);
    //   });
    //
    // svg.call(zoomBehavior);
  }, [data, dimensions, currentZoomState, minMax, radius, fontSize]);

  return (
    <>
    <div ref={wrapperRef}>
      <svg ref={svgRef}>
        <g className="x-axis" />
      </svg>
    </div>
    <div className="images">
      {Data ? <Image urls={Data.imgs}/> : null}
    </div>
    </>
  );
};

export default Graphic;
