/*
   generates links between all linked nodes and
   returns a link object with link properties.
*/

function generateLinks(data) {
  const links = []; //array of all node links

  /// IMPORTANT: all ending nodes must be marked w target:null ///

  data.forEach((src, i) => {
    if (src.length) {
      src.forEach((item) => {
        if (item.target) { //check if detached
          if (typeof(item.target) !== "number") { //for nodes w multiple targets, create link for each target
            item.target.forEach((n) => {
              console.log(src.find(x => x.id === n));
              // links.push({source:item, target:src.find(x => x.id === n), color:item.lineColor ? item.lineColor : item.color ? item.color : null, width:item.lineWidth});
            });
          } else {
            //if lineColor not defined, line takes the color of the previous point
            links.push({source:item, target:src.find(n => n.id === item.target), color:item.lineColor ? item.lineColor : item.color ? item.color : null, width:item.lineWidth})
          };
        }
      })
    }
  });
  return links;
};

export default generateLinks;
