import React, { useState } from 'react';

/*
   UI and function for adding new zones.
*/

function EditScaleForm({scales, setScales, setToggle, activeScale, setActiveScale}) {
  const [select, setSelect] = useState(activeScale);
  //go thru list of scales in ChronoJSON and create a selection entry for each
  const scalesList = Object.keys(scales.x).map((key, i) => {
    return <option value={key} key={i}>{key}</option>
  });
  const [min, setMin] = useState(scales.x[select].min);
  const [max, setMax] = useState(scales.x[select].max);
  const [units, setUnits] = useState(scales.x[select].units);
  const [yPos, setYPos] = useState(scales.x[select].yPos);
  //optional parameters
  const [segmentsNum, setSegmentNum] = useState([]);


  const handleSubmit = (e) => {
    //setHistory
    const newScales = {...scales};
    //update existing scale
    newScales[select] = {
      "min": parseFloat(min),
      "max": parseFloat(max),
      "units": units,
      "yPos": parseFloat(yPos),
      "segments": [] //todo
    }
    setScales(newScales);
    setActiveScale(select);
    setToggle(null);
    e.preventDefault();
  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right uiButton closeButton" onClick={handleClose}>x</button><br/>
      <label>
        Scale<span className="redText">* </span>
        <select name="scales" value={select} onChange={(e) => setSelect(e.target.value)}>
          {scalesList}
        </select>
      </label><br/>
      <label>
        Minimum Value<span className="redText">* </span>
        <input type="text"
          placeholder="#s for now"
          value={min}
          onChange={(e) => setMin(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Maximum Value<span className="redText">* </span>
        <input type="text"
          placeholder="#s for now"
          value={max}
          onChange={(e) => setMax(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Units Label<span className="redText">* </span>
        <input type="text"
          placeholder="ie: Years"
          value={units}
          onChange={(e) => setUnits(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Vertical Position<span className="redText">* </span>
        <input
          type="text"
          placeholder="0 (top) - 1 (bottom)"
          value={yPos}
          onChange={(e) => setYPos(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Segments
        <input
          type="number"
          placeholder="granularities within scale"
          value={segmentsNum}
          onChange={(e) => setSegmentNum(e.target.value)}
        />
      </label><br/>
      <input className="uiButton" type="submit" value="Submit"/>
    </form>
  )
};

export default EditScaleForm;
