import React, { useState } from 'react';

function EditForm(props) {
  const [editMode, setEditMode] = useState(false);
  const [value, setValue] = useState(null);

  const handleEdit = (e) => {
    /*
    1. clicking edit replaces text with a field with value = og value
    2. edit button changes into submit
    3. upon submit, locate property in JSON via key
    4. then set key value to field value
    */

  }
  return (
    editMode ?
    <form onSubmit={handleEdit}>
      <input
        type={props.inputType}
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <input type="submit" value="Submit"/>
    </form>
    : <> {props.property} <button onClick={() => setEditMode(true)}>Edit</button></>
  )
};

export default EditForm;
