const InputFormScales = ({scales, value, handleChange}) => {
  const scalesList = Object.keys(scales).map((key, i) => {
    return <option value={key} key={i}>{key}</option>
  });

  return (
    <label>
      Scale<span className="redText">* </span>
      <select name="scale" value={value} onChange={handleChange}>
        {scalesList}
      </select>
    </label>
  )
};

export default InputFormScales;
