import React, { useState } from 'react';

/*
   UI and function for adding new intervals.
*/

function NewIntervalForm({intervals, setIntervals, setToggle}) {
  const [label, setLabel] = useState("");
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  //optional parameters
  const [yStart, setYStart] = useState("");
  const [yEnd, setYEnd] = useState("");
  const [color, setColor] = useState("");
  const [opacity, setOpacity] = useState("");

  // const handleChange = (e) => {
  //   //if it's a checkbox, evaluate if checked. otherwise, evaluate the value
  //   const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
  //   const name = e.target.name;
  //   // console.log(value);
  //   // setLabel(value);
  //   this["set"+name](value);
  //
  //   // setValue({
  //   //   [name]: value
  //   // });
  // }

  const handleSubmit = (e) => {
    //generate new interval
    const interval = {
      "id": intervals.length ? intervals[intervals.length-1].id + 1 : 0,
      "name": label,
      "start": parseFloat(start), //cases where start > end?
      "end": parseFloat(end),
      ...(yStart && {"yStart": yStart}),
      ...(yEnd && {"yEnd": yEnd}),
      ...(color && {"color": color}),
      ...(opacity && {"opacity": opacity})
    }
    const newIntervals = [...intervals, interval];
    setIntervals(newIntervals);
    e.preventDefault();
  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <label>
        Label<span className="red">* </span>
        <input type="text"
          placeholder="any unicode characters"
          value={label}
          onChange={(e) => setLabel(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Time Start<span className="red">* </span>
        <input type="text"
          placeholder="#s, dates, logical operators"
          value={start}
          onChange={(e) => setStart(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Time End<span className="red">* </span>
        <input type="text"
          placeholder="#s, dates, logical operators"
          value={end}
          onChange={(e) => setEnd(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Y Start
        <input
          type="text"
          placeholder="#s, dates, logical operators"
          value={yStart}
          onChange={(e) => setYStart(e.target.value)}
        />
      </label><br/>
      <label>
        Y End
        <input
          type="text"
          placeholder="#s, dates, logical operators"
          value={yEnd}
          onChange={(e) => setYEnd(e.target.value)}
        />
      </label><br/>
      <label>
        Color
        <input
          type="text"
          placeholder="blue, #4DA6FF, etc."
          value={color}
          onChange={(e) => setColor(e.target.value)}
        />
      </label><br/>
      <label>
        Opacity
        <input
          type="number"
          placeholder="0 (transparent) - 1 (opaque)"
          value={opacity}
          onChange={(e) => setOpacity(e.target.value)}
        />
      </label><br/>
      <input type="submit" value="Submit"/>
    </form>
  )
};

export default NewIntervalForm;
