import React, { useState, useEffect, useRef } from 'react';
import { select, scaleLinear, axisBottom, linkHorizontal, format, drag, zoom } from "d3";
import useResizeObserver from "./useResizeObserver";
import generateLinks from "./generateLinks";
import findMinMax from "./findMinMax";

function Graphic({data, setData, activeLayer, intervals, setIntervals, radius, setInfo, showLabel}) {

  //all data is passed down from and synced to App.js
  const activeData = activeLayer ? data.filter(d => d.layer === activeLayer) : data;

  //initialize reference object to pass to svg in React Dom
  const svgRef = useRef();
  //svg wrapped in div bc otherwise resize observer callback won't work
  const wrapperRef = useRef();
  const dimensions = useResizeObserver(wrapperRef);
  const [currentZoomState, setCurrentZoomState] = useState();

  //initialize links between nodes
  const links = generateLinks(activeData);
  const minMax = findMinMax(data);

  //initialize bottom axis label
  const [axisLabel, setAxisLabel] = useState("Years (AD)");

  //all d3 shennanigans goes in this hook. called initially and on every data change
  useEffect(() => {
    const svg = select(svgRef.current); //"svg" is the current version of svgRef data
    //prevents crashing when useResizeObserver returns null before svg is rendered
    if (!dimensions) return;
    //prevents crashing before min and max are calculated
    if (!minMax) return;
    if (!links) return;

    //---scales---//
    const padding = .05;
    const breakpoint = 5;

    const rangeX = minMax[1]-minMax[0]; //0 is minX, 1 is maxX (reference findMinMax)
    const rangeY = minMax[3]-minMax[2];
    const minRangeX = minMax[0]-rangeX*padding;
    const maxRangeX = minMax[1]+rangeX*padding;
    const minRangeY = minMax[2]-rangeY*padding; //leave space for toolbar
    const maxRangeY = minMax[3]+rangeY*padding;

    const xScale = scaleLinear()
      .domain([minRangeX, maxRangeX]) //scaleLinear takes domain (data values) & maps onto range (screen pixel values)
      .range([0, dimensions.width]);
    const yScale = scaleLinear()
      //if difference in y vaules greater than 5, resize to min and max
      .domain((rangeY>breakpoint) ? [minRangeY, maxRangeY] : [minMax[2]-breakpoint, minMax[3]+breakpoint])
      .range([0, dimensions.height]);
    //inverse scales for drag behavior
    const xScaleInverse = scaleLinear()
      .domain([-dimensions.width, dimensions.width])
      .range([-(maxRangeX-minRangeX), maxRangeX-minRangeX]); //maxRangeX-minRangeX gives section of x values displayed onscreen
    const yScaleInverse = scaleLinear()
      .domain([-dimensions.height, dimensions.height])
      .range((rangeY>breakpoint) ? [-(maxRangeY-minRangeY), maxRangeY-minRangeY] : [-breakpoint*5, breakpoint*5]);

    if (currentZoomState) {
      const newXScale = currentZoomState.rescaleX(xScale); //zoom state comes w methods for transformations
      xScale.domain(newXScale.domain());
      xScaleInverse.range([
        -(newXScale.domain()[1]-newXScale.domain()[0]),
        (newXScale.domain()[1]-newXScale.domain()[0])
      ]);
    };

    //---draw intervals---//
    if (intervals.length) {
      svg
      .selectAll(".intervals")
      .data(intervals)
      .join("rect")
      .attr("class", "intervals")
      .attr("x", n => xScale(n.start))
      // .attr("y", n => n.yStart ? yScale(n.yStart) : yScale(minMax[2]))
      .attr("y", n => n.yStart ? yScale(n.yStart) : 0) //todo
      .attr("width", n => xScale(n.end)-xScale(n.start))
      .attr("height", n => {
        if (n.yStart & n.yEnd) {
          return (yScale(n.yEnd)-yScale(n.yStart))
        } else if (n.yStart) {
          return (yScale(minMax[3]+10)-yScale(n.yStart))
        } else {
          return (yScale(minMax[3]+10)) //todo
          // return (yScale(minMax[3])-yScale(minMax[2]))
        }})
      .attr("opacity", d => d.opacity ? d.opacity : 0.5)
      .attr("fill", d => d.color ? d.color : "#999");
    }

    //---draw bottom axis---//
    const xAxis = axisBottom(xScale)
      // .tickSize(-dimensions.height) //tick extends to entire height of svg
      .tickFormat(format('')); //gets rid of , for 1000s groupings

    svg
      .select(".x-axis") //selects and replaces .x-axis
      .style("transform", `translateY(${dimensions.height}px)`)
      .call(xAxis)
      .raise()
      .call(g => g.select(".domain") //removes solid black axis bar
        .remove())
      .call(g => g.selectAll(".tick line") //styles individual ticks
        .attr("stroke-opacity", 0.5))
        // .attr("stroke-dasharray", "2,2"))
      .call(g => g.selectAll(".tick text")
        .attr("y", 10) //todo?
        .attr("opacity", 0.5));
        // .attr("font-size", fontSize*.8)); //replace class "x-axis" w xAxis

    //TODO: enter & exit
    svg
      // .selectAll(".axislabel")
      // .data([1])
      // .join("text")
      .enter().append("text")
      .text(axisLabel)
      .attr("class", "axisLabel smallTxt")
      .attr("x", dimensions.width/2)
      .attr("y", dimensions.height*1.08)
      .attr("text-anchor", "middle")
      .attr("opacity", 0.5)
      .exit().remove();

    //--drag behavior--//
    const dragged = drag()
      // .on("start", e => {
      //   select(this).attr("stroke", "black");
      // })
      .on("drag", e => {
        //todo
        const dx = Math.round(xScaleInverse(e.dx)*100)/100;
        const dy = Math.round(yScaleInverse(e.dy)*100)/100;
        const newNodes = [...data]; //always make a copy when updating array
        //find array element in data with matching ID and set new x y coordinates
        newNodes.find(n => n.id === e.subject.id).x += dx;
        newNodes.find(n => n.id === e.subject.id).y += dy;
        setData(newNodes);
      });


    //---draw links---//
    const linker = linkHorizontal()
      .source(link => link.source)
      .target(link => link.target)
      .x(node => xScale(node.x))
      .y(node => yScale(node.y));

    svg
      .selectAll(".link")
      .data(links)
      .join("path")
      .raise()
      .attr("class", "link pointer")
      .attr("d", linker)
      .attr("fill", "none")
      .attr("stroke", n => n.color? n.color : "black")
      .attr("stroke-width", n => n.width? n.width : radius/3)
      .on("click", (e, target) => {
        setInfo({position: [e.x, e.y], target: target, type: "link"}); //displays line properties when clicked
        e.stopPropagation();
      });


    //---draw points---//
    svg
      .selectAll(".node")
      .data(activeData)
      .join("circle")
      .raise()
      .attr("class", "node pointer")
      .attr("r", n => n.radius? n.radius : radius) //todo for accessibility
      .attr("fill", d => d.color ? d.color : "black") //if it has a color other than black, use that
      .attr("cx", d => xScale(d.x))
      .attr("cy", d => yScale(d.y))
      .call(dragged)
      .on("click", (e, target) => {
        setInfo({position: [e.x, e.y], target: target}); //displays node properties when clicked
        e.stopPropagation(); //stops deselect when bg is clicked
      });


    //---draw labels---//
    svg
      .selectAll(".label")
      .data(activeData)
      .join("text")
      .raise()
      .attr("class", "label")
      .text(node => showLabel ? node.name : node.id)
      .attr("x", node => xScale(node.x))
      .attr("y", node => yScale(node.y + (rangeY>breakpoint ? rangeY*.06 : breakpoint/5))) //vertical offset. see scales
      .attr("text-anchor", "middle");
      // .attr("font-size", fontSize);


    //---zoom---//
    const zoomBehavior = zoom()
      .scaleExtent([0.25, 5]) //can zoom 2x smaller and 5x bigger
      .translateExtent([[-100, 0], [dimensions.width+100, dimensions.height]]) //limits how far graph can pan
      .on("zoom", e => {
        const zoomState = e.transform; //returns zoom transformations as an object
        setCurrentZoomState(zoomState);
      });


    svg.on("click", () => setInfo(null));
    svg.call(zoomBehavior);

  }, [data, intervals, activeLayer, dimensions, currentZoomState, radius, showLabel]);

  return (
    <div ref={wrapperRef} className="svgWrapper">
      <svg ref={svgRef}>
        <g className="x-axis" />
      </svg>
    </div>
  );
};


export default Graphic;
