import React, { useState, useEffect, useRef } from 'react';
import { select, scaleLinear, scaleOrdinal, axisBottom, linkHorizontal, format, drag, zoom, min, max, symbol, symbols } from "d3";
import useResizeObserver from "./useResizeObserver";
import generateLinks from "./generateLinks";

function Graphic({data, setData, activeLayer, intervals, setIntervals, radius, info, setInfo, showLabel, toggle, scales, setScales}) {

  //all data is passed down from and synced to App.js
  const activeData = activeLayer ? data.filter(d => d.layer === activeLayer) : data;

  //is logical operators active?
  const logic = isNaN(data[0].x); //Todo

  //initialize reference object to pass to svg in React Dom
  const svgRef = useRef();
  //svg wrapped in div bc otherwise resize observer callback won't work
  const wrapperRef = useRef();
  const dimensions = useResizeObserver(wrapperRef);
  const [currentZoomState, setCurrentZoomState] = useState();

  //initialize links between nodes
  const links = generateLinks(activeData);

  //initialize bottom axis label
  const [axisLabel, setAxisLabel] = useState("Years (AD)");

  //all d3 shennanigans goes in this hook. called initially and on every data change
  useEffect(() => {
    const svg = select(svgRef.current); //"svg" is the current version of svgRef data
    //prevents crashing when useResizeObserver returns null before svg is rendered
    if (!dimensions) return;

    //---scales---//

    //if time matches logical operator keyword, translate into index values, then map
    const logicScale = scaleOrdinal(["before", "now", "after"], [-1, 0, 1]).unknown(0);

    const breakpoint = 5;


    const xMin = min(data.map(({x}) => logic ? logicScale(x) : x));
    const xMax = max(data.map(({x}) => logic ? logicScale(x) : x));
    const yMin = min(data.map(({y}) => y));
    const yMax = max(data.map(({y}) => y));
    const rangeX = xMax-xMin; //0 is minX, 1 is maxX (reference findMinMax)
    const rangeY = yMax-yMin;


    //TODO: scaleTime()

    //for multiple scales, generate a new xScale for each x/y min - x/y max
    /*
    - Allow users to specify scale. x&y min max and units
    - for each scale, generateScale()
    */

    function generateScales(currentScale) {

      const x = scaleLinear()
        .domain([currentScale.min, currentScale.max]) //scaleLinear takes domain (data values) & maps onto range (screen pixel values)
        .range([0, dimensions.width])
      const y = scaleLinear()
        //if difference in y vaules greater than 5, resize to min and max
        .domain((rangeY>breakpoint) ? [yMin, yMax] : [yMin-breakpoint, yMax+breakpoint])
        .range([0, dimensions.height])
      const xInverse = scaleLinear()
        .domain([-dimensions.width, dimensions.width])
        .range([-(currentScale.max-currentScale.min), (currentScale.max-currentScale.min)]) //maxRangeX-minRangeX gives section of x values displayed onscreen
      const yInverse = scaleLinear()
        .domain([-dimensions.height, dimensions.height])
        .range((rangeY>breakpoint) ? [-rangeY, rangeY] : [-breakpoint*5, breakpoint*5])


      return {
        x, y, xInverse, yInverse
      };
    }

    //scales values to screen pixels

    const xScale = scaleLinear()
      .domain([xMin, xMax]) //scaleLinear takes domain (data values) & maps onto range (screen pixel values)
      .range([0, dimensions.width]);
    const yScale = scaleLinear()
      //if difference in y vaules greater than 5, resize to min and max
      .domain((rangeY>breakpoint) ? [yMin, yMax] : [yMin-breakpoint, yMax+breakpoint])
      .range([0, dimensions.height]);
    //inverse scales for drag behavior
    const xScaleInverse = scaleLinear()
      .domain([-dimensions.width, dimensions.width])
      .range([-rangeX, rangeX]); //maxRangeX-minRangeX gives section of x values displayed onscreen
    const yScaleInverse = scaleLinear()
      .domain([-dimensions.height, dimensions.height])
      .range((rangeY>breakpoint) ? [-rangeY, rangeY] : [-breakpoint*5, breakpoint*5]);

    //todo
    if (currentZoomState) {
      const newXScale = currentZoomState.rescaleX(generateScales(scales.smalls).x); //zoom state comes w methods for transformations
      generateScales(scales.smalls).x.domain(newXScale.domain());
      generateScales(scales.smalls).xInverse.range([
        -(newXScale.domain()[1]-newXScale.domain()[0]),
        (newXScale.domain()[1]-newXScale.domain()[0])
      ]);
      svg
        .selectAll(".remove")
        .remove();
    };


    //---draw intervals---//
    if (intervals.length) {
      svg
      .selectAll(".intervals")
      .data(intervals)
      .join("rect")
      .attr("class", "intervals")
      .attr("x", n => generateScales(scales[n.scale]).x(n.start))
      // .attr("y", n => n.yStart ? yScale(n.yStart) : yScale(minMax[2]))
      .attr("y", n => n.yStart ? yScale(n.yStart) : 0) //todo
      .attr("width", n => generateScales(scales[n.scale]).x(n.end)-generateScales(scales[n.scale]).x(n.start))
      .attr("height", n => {
        if (n.yStart & n.yEnd) {
          return (yScale(n.yEnd)-yScale(n.yStart))
        } else if (n.yStart) {
          return (yScale(yMax+10)-yScale(n.yStart))
        } else {
          return (yScale(yMax+10)) //todo
          // return (yScale(minMax[3])-yScale(minMax[2]))
        }})
      .attr("opacity", d => d.opacity ? d.opacity : 0.5)
      .attr("fill", d => d.color ? d.color : "#999")
      .on("click", (e, target) => {
        //displays line properties when clicked
        setInfo({position: [e.x, e.y], target: target, type: "interval"});  //todo: set info to null when clicking again
        e.stopPropagation();
      });
    }

    //---draw bottom axis---//

    //for each active scale, generate an x axis and place on the screen according to proportion of screen height/width as defined in JSON
    Object.keys(scales).map(scale => {

        const xAxis = axisBottom(generateScales(scales[scale]).x)
        .tickFormat(format(''));

        svg
          .append("g") //todo
          .attr("class", "remove")
          .style("transform", `translateY(${dimensions.height*scales[scale].yProportion}px)`)
          .call(xAxis)
          .raise()
          .call(g => g.select(".domain") //removes solid black axis bar
            .remove())
          .call(g => g.selectAll(".tick line") //styles individual ticks
            .attr("stroke-opacity", 0.5))
          .call(g => g.selectAll(".tick text")
            .attr("y", 10) //todo?
            .attr("opacity", 0.5));

      });


    // const xAxis = axisBottom(generateScales(scales.smalls).x)
    //   // .tickSize(-dimensions.height) //tick extends to entire height of svg
    //   .tickValues(logic ? [-1, 0, 1] : null)
    //   .tickFormat(logic ? x => scaleOrdinal([-1, 0, 1], ["before", "now", "after"])(x) : format('')); //gets rid of , for 1000s groupings

    // svg
    //   .select(".x-axis") //selects and replaces .x-axis
    //   .style("transform", `translateY(${dimensions.height}px)`)
    //   .call(xAxis)
    //   .raise()
      // .call(g => g.select(".domain") //removes solid black axis bar
      //   .remove())
      // .call(g => g.selectAll(".tick line") //styles individual ticks
      //   .attr("stroke-opacity", 0.5))
      //   // .attr("stroke-dasharray", "2,2"))
      // .call(g => g.selectAll(".tick text")
      //   .attr("y", 10) //todo?
      //   .attr("opacity", 0.5));
      //   // .attr("font-size", fontSize*.8)); //replace class "x-axis" w xAxis
    //

    //TODO: enter & exit
    svg
      // .selectAll(".axislabel")
      // .data([1])
      // .join("text")
      .append("text")
      .text(axisLabel)
      .attr("class", "axisLabel smallTxt remove")
      .attr("x", dimensions.width/2)
      .attr("y", dimensions.height*1.08)
      .attr("text-anchor", "middle")
      .attr("opacity", 0.5);

    //--drag behavior--//
    const dragged = drag()
      // .on("start", e => {
      //   select(this).attr("stroke", "black");
      // })
      .on("drag", e => {
        //todo
        const dx = Math.round(generateScales(scales.smalls).xInverse(e.dx)*100)/100;
        const dy = Math.round(yScaleInverse(e.dy)*100)/100;
        const newNodes = [...data]; //always make a copy when updating array
        //find array element in data with matching ID and set new x y coordinates
        newNodes.find(n => n.id === e.subject.id).x += dx;
        newNodes.find(n => n.id === e.subject.id).y += dy;
        setData(newNodes);
      });

    //---draw links---//
    const linker = linkHorizontal()
      .source(link => link.source)
      .target(link => link.target)
      .x(n => generateScales(scales[n.scale]).x(logic ? logicScale(n.x) : n.x))
      .y(n => yScale(n.y));

    svg
      .selectAll(".link")
      .data(links)
      .join("path")
      .raise()
      .attr("class", "link pointer")
      .attr("d", linker)
      .attr("fill", "none")
      .attr("stroke", n => n.color? n.color : "black")
      .attr("stroke-width", n => n.width ? n.width : radius/3)
      .attr("stroke-opacity", n => n.opacity ? n.opacity : 1)
      .attr("stroke-dasharray", n => n.dashed ? n.dashed : null)
      .on("click", (e, target) => {
        setInfo({position: [e.x, e.y], target: target, type: "link"}); //displays line properties when clicked
        e.stopPropagation();
      });


    //---draw points---//
    svg
      .selectAll(".node")
      .data(activeData.filter(d => d.inflectionSemantic!=="cancelled"))
      .join("circle")
      .raise()
      .attr("class", "node pointer")
      .attr("r", n => n.radius? n.radius : radius) //todo for accessibility
      .attr("fill", d => d.color ? d.color : "black") //if it has a color other than black, use that
      .attr("opacity", d => d.opacity ? d.opacity : 1)
      .attr("cx", d => generateScales(scales[d.scale]).x(logic ? logicScale(d.x) : d.x))
      .attr("cy", d => yScale(d.y))
      .call(dragged)
      .on("click", (e, target) => {
        if (toggle==="cancelled") {
          const newNodes = [...data];
          const node = newNodes.find(n => n.id === target.id);
          node.inflectionSemantic = "cancelled";
          setData(newNodes);
        } else {
          setInfo({position: [e.x, e.y], target: target}); //displays node properties when clicked
        }
        e.stopPropagation(); //stops deselect when bg is clicked
      });

    //---initialize custom symbols---//
    const cancelledSymbol = {
      draw: (context, size) => {
        let s = Math.sqrt(size)/2;
            context.moveTo(s,0);
            context.lineTo(s*2,s);
            context.lineTo(s,s*2);
            context.lineTo(0,s);
            context.lineTo(-s,s*2);
            context.lineTo(-s*2,s);
            context.lineTo(-s,0);
            context.lineTo(-s*2,-s);
            context.lineTo(-s,-s*2);
            context.lineTo(0,-s);
            context.lineTo(s,-s*2);
            context.lineTo(s*2,-s);
            context.closePath();
      }
    }

    //---draw symbols---//
    svg
      .selectAll(".cancelled")
      .data(activeData.filter(d => d.inflectionSemantic==="cancelled"))
      .join("path")
      .raise()
      .attr("class", "cancelled pointer")
      .attr("d", symbol(cancelledSymbol, radius*radius*5))
      .attr("transform", n => `translate(${generateScales(scales[n.scale]).x(n.x)}, ${yScale(n.y)})`)
      .on("click", (e, target) => {
        if (toggle==="cancelled") {
          const newNodes = [...data];
          const node = newNodes.find(n => n.id === target.id);
          delete node.inflectionSemantic;
          setData(newNodes);
        } else {
          setInfo({position: [e.x, e.y], target: target}); //displays node properties when clicked
        }
        e.stopPropagation();
      });
      // .attr("transform", "rotate(90)");


    //---draw labels---//
    svg
      .selectAll(".label")
      .data(activeData)
      .join("text")
      .raise()
      .attr("class", "label")
      .text(node => showLabel ? node.name : node.id)
      .attr("x", node => generateScales(scales[node.scale]).x(logic ? logicScale(node.x) : node.x))
      .attr("y", node => yScale(node.y + (rangeY>breakpoint ? rangeY*.06 : breakpoint/5))) //vertical offset. see scales
      .attr("text-anchor", "middle");
      // .attr("font-size", fontSize);


    //---zoom---//
    const zoomBehavior = zoom()
      .scaleExtent([0.25, 5]) //can zoom 2x smaller and 5x bigger
      .translateExtent([[-100, 0], [dimensions.width+100, dimensions.height]]) //limits how far graph can pan
      .on("zoom", e => {
        const zoomState = e.transform; //returns zoom transformations as an object
        setCurrentZoomState(zoomState);
      });

    svg.on("click", () => setInfo(null));
    svg.call(zoomBehavior);

  }, [data, setData, intervals, scales, activeLayer, activeData, dimensions, links, logic, currentZoomState, setInfo, radius, showLabel]);

  return (
    <div ref={wrapperRef} className="svgWrapper">
      <svg id="graphic" ref={svgRef}>
        <g className="cancelled" />
      </svg>
    </div>
  );
};


export default Graphic;
