  return (
    <form className="ui absolute center container">
      <label>
        Label<span className="redText">* </span>
        <input
          type="text"
          placeholder="any unicode character"
          value={text}
          onChange={handleChange}
        />
      </label><br/>
      <b>Start:</b><br/>
      <label>
        Time<span className="redText">* </span>
        <input
          type="text"
          placeholder="numbers only"
          value={xValue1}
          onChange={handleChange}
          required
        />
      </label><br/>
      <label>
        y Position
        <input
          type="text"
          placeholder="#s only (default 0)"
          value={yValue1}
          onChange={handleChange}
        />
      </label><br/>
      <b>End:</b><br/>
      <label>
        Time<span className="redText">* </span>
        <input
          type="text"
          placeholder="numbers only"
          value={xValue2}
          onChange={handleChange}
          required
        />
      </label><br/>
      <label>
        y Position
        <input
          type="text"
          placeholder="#s only (default 0)"
          value={yValue2}
          onChange={handleChange}
        />
      </label><br/>
      <b>Properties:</b><br/>
      <InputFormScale
        scales={scales.x}
        label="X Scale"
        name="xSelect"
        value={values.xSelect}
        onChange={handleChange}
      />
      <label>
        Color
        <input
          type="text"
          placeholder="blue, #4DA6FF, etc."
          value={color}
          onChange={handleChange}
        />
      </label><br/>
      <label>
        Width
        <input
          type="number"
          placeholder="1.67"
          value={width}
          onChange={handleChange}
        />
      </label><br/>
      <label>
        Certainty
        <input
          type="number"
          placeholder="0-1"
          value={opacity}
          onChange={handleChange}
        />
      </label><br/>
      <div className="flexCenter">
        <button className="buttonSecondary" onClick={this.handleClose}>Cancel</button> &nbsp;
        <button className="buttonSecondary" onClick={this.handleSubmit}>Submit</button>
      </div>
    </form>
  )
}

export default NewIntervalForm;
