import HandsFree from 'handsfree';
import Model from '@mediapipe/hands';

//https://handsfree.js.org/ref/model/hands.html

function Hands(props) {
  const handleClick = (e) => {
    if (!props.enabled){
      props.setEnabled(true);
      const hands = new HandsFree({
        hands:{
          enabled: props.enabled,
          maxNumHands: 2
        }
      });

      hands.start();
      hands.on('data', ({data}) => {
        console.log(data);
      });
    } else {
      hands.stop();
      props.setEnabled(false);
    }
  }


  //
  return (
    <button onClick={handleClick}>
      enable hands
    </button>
  )

};

export default Hands;
