import React, { useState } from 'react';

//AKA the navbar, holds all the tools, settings, and whatnot on the top of the page

function ToolsBar(props) {
  const [content, setContent] = useState(null);

  const handleClick = (e) => {
    if (content) { //if there is already a dropdown, hide it
      setContent(null);
    } else {
      switch (e.target.id) { //depending on what button is clicked, display dropdown etc.
        case 'temporalObject':
        // <li><button id="addInstant" onClick={handleClick} title="Instant"><svg><circle></circle></svg></button></li>
        // <li><button id="addInterval" onClick={handleClick} title="Interval"><svg><path></path></svg></button></li>

          setContent(
            <ul>
              <li><button id="addInstant" onClick={handleClick} title="Instant">Instant</button></li>
              <li><button id="addInterval" onClick={handleClick} title="Interval">Interval</button></li>
              <li><button id="addZone" onClick={handleClick} title="Zone">Zone</button></li>
            </ul>
          );
        break
        case 'scales':
          setContent(
            <ul>
              <li><button id="selectScale" onClick={handleClick} title="Select Active Scale">Select Active Scale</button></li>
              <li><button id="addScale" onClick={handleClick} title="Create Scale">Create Scale</button></li>
              <li><button id="addInterval" onClick={handleClick} title="Expand Scale">Expand Scale</button></li>
            </ul>
          );
        break
        case 'inflections':
          //canceelled functionality in Graphic.js
          setContent(
            <ul>
              <li>Semantic</li>
              <li><button id="cancelled" onClick={handleClick}>Cancelled</button></li>
              <li><button id="importance" onClick={handleClick}>Importance</button></li>
              <li><button id="certainty" onClick={handleClick}>Certainty</button></li>
              <li><button id="foreshadowing" onClick={handleClick}>Foreshadowing</button></li>
              <li>Syntactic</li>
              <li><button id="connection" onClick={handleClick}>Connection</button></li>
              <li><button>Causality</button></li>
            </ul>
          );
        break
        case 'toggle':
          setContent(
            <ul>
              <li>Graphics</li>
              <li><button id="scrubber" onClick={handleClick}>Scrubber</button></li>
              <li><button>Scale Format</button></li>
              <li><button id="layers" onClick={handleClick}>Layers</button></li>
              <li>UI</li>
              <li><button id="showLabel" onClick={handleClick}>{props.showLabel ? "Show IDs" : "Show Labels"}</button></li>
              <li><button id="legend" onClick={handleClick}>Legend</button></li>
            </ul>
          );
        break
        case 'file':
          setContent(
            <ul>
              <li><button id="import">Import Data</button></li>
              <li><button id="save" onClick={handleClick}>Save & Export</button></li>
              <li><button id="importImg">Import Image</button></li>
            </ul>
          );
        break
        case 'menu':
          setContent(
            <ul>
              <li>Edit</li>
              <li><button id="undo">Undo</button></li>
              <li><button id="redo">Redo</button></li>
              <li>Settings</li>
              <li><button id="acessibility">Accessibility</button></li>
              <li><button id="theme">Theme</button></li>
              <li>NL Chrono</li>
              <li><button id="about">About</button></li>
              <li><button id="documentation">Documentation</button></li>
            </ul>
          );
        break
        case 'showLabel':
          props.showLabel ?
            props.setShowLabel(false)
          : props.setShowLabel(true);
          setContent(null);
        break
        default:
          setContent(null);
          if (props.toggle && props.toggle===e.target.id) { //if toggle tool that is already active, deactivate tool
            props.setToggle(null);
          } else { //otherwise, activate tool
            props.setToggle(e.target.id);
          }
      }
    };
    e.preventDefault();
  }

  return (
    <>
      <nav className="ui container flexRow flexSpace">
        <div className="halfWidth">
          <ul className="inline">
            <li className="dropdown">
              <button id="file" onClick={handleClick} title="File"><span className="material-icons-round">description </span></button>
              <div className="dropdown-content">
              </div>
            </li>
            <li><button id="temporalObject" onClick={handleClick} title="Temporal Objects"><span className="material-icons-round">horizontal_rule</span></button></li>
            <li><button id="inflections" onClick={handleClick} title="Inflections"><span className="material-icons-round">clear</span></button></li>
            <li><button id="scales" onClick={handleClick} title="Scales"><span className="material-icons-round">more_horiz</span></button></li>
          </ul>
        </div>
        <div className="halfWidth right">
          <ul className="inline">
            {props.toggle && <li>Currently active: {props.toggle}</li>}
            <li><button id="pan" onClick={handleClick} title="Pan"><span className="material-icons-round">pan_tool</span></button></li>
            <li><button id="toggle" onClick={handleClick} title="Views"><span className="material-icons-round">visibility</span></button></li>
            <li><button id="menu" onClick={handleClick} title="Menu"><span className="material-icons-round">menu</span></button></li>

          </ul>
        </div>
      </nav>
      {content && <div className="ui absolute" style={{left: 0 + '%'}}>
        {content}
      </div>}
    </>
  );
}

export default ToolsBar;
