import React, { useState, useEffect, useRef } from 'react';
import { select, scaleLinear, scaleOrdinal, axisBottom, linkHorizontal, format, drag, zoom, min, max, symbol, symbols } from "d3";
import useResizeObserver from "./useResizeObserver";
import generateLinks from "./generateLinks";

function Graphic({
  data, setData,
  activeLayer,
  intervals, setIntervals,
  radius,
  info, setInfo,
  setInflectTarget,
  showLabel,
  toggle,
  scales, setScales,
  syntacticOrder, setSyntacticOrder
}) {

  //all data is passed down from and synced to App.js
  const activeData = activeLayer ? data.filter(d => d.layer === activeLayer) : data;

  //is logical operators active?
  const logic = isNaN(data[0].x); //Todo

  //initialize reference object to pass to svg in React Dom
  const svgRef = useRef();
  //svg wrapped in div bc otherwise resize observer callback won't work
  const wrapperRef = useRef();
  const dimensions = useResizeObserver(wrapperRef);
  const [currentZoomState, setCurrentZoomState] = useState();

  // //initialize links between nodes
  const links = generateLinks(activeData);

  //all d3 shennanigans goes in this hook. called initially and on every data change
  useEffect(() => {
    const svg = select(svgRef.current); //"svg" is the current version of svgRef data
    //prevents crashing when useResizeObserver returns null before svg is rendered
    if (!dimensions) return;
    //removes svgs added using append()
    svg
      .selectAll(".remove")
      .remove();

    //initialize inflections for .selectAll
    //add cancelled class

    //---scales---//

    //if time matches logical operator keyword, translate into index values, then map
    const logicScale = scaleOrdinal(["before", "now", "after"], [-1, 0, 1]).unknown(0);

    const breakpoint = 5;


    const xMin = min(data.map(({x}) => logic ? logicScale(x) : x));
    const xMax = max(data.map(({x}) => logic ? logicScale(x) : x));
    const yMin = min(data.map(({y}) => y));
    const yMax = max(data.map(({y}) => y));
    const rangeX = xMax-xMin; //0 is minX, 1 is maxX (reference findMinMax)
    const rangeY = yMax-yMin;


    //TODO: scaleTime()

    //for multiple scales, generate a new scale for each x/y min - x/y max
    /*
    - Allow users to specify scale. x&y min max and units
    - for each scale, generateScale()

    */

    function generateScales(currentScale) {

      const x = scaleLinear()
        .domain([currentScale.min, currentScale.max]) //scaleLinear takes domain (data values) & maps onto range (screen pixel values)
        .range([0, dimensions.width])
      const y = scaleLinear()
        //if difference in y vaules greater than 5, resize to min and max
        .domain((rangeY>breakpoint) ? [yMin, yMax] : [yMin-breakpoint, yMax+breakpoint])
        .range([0, dimensions.height])
      const xInverse = scaleLinear()
        .domain([-dimensions.width, dimensions.width])
        .range([-(currentScale.max-currentScale.min), (currentScale.max-currentScale.min)]) //maxRangeX-minRangeX gives section of x values displayed onscreen
      const yInverse = scaleLinear()
        .domain([-dimensions.height, dimensions.height])
        .range((rangeY>breakpoint) ? [-rangeY, rangeY] : [-breakpoint*5, breakpoint*5])


      return {
        x, y, xInverse, yInverse
      };
    }

    //scales values to screen pixels
    /*
    {
      biggie: {
        ...
        scale: generateScale()
      }
      ...
    }
    */
    // const xScales = () => {
    //   Object.keys(scales).forEach(key => { //for every scale in scales data, generate an xScale
    //     // const thisScale = scales[key];
    //     scales[key] = {...scales[key], scale:generateScales(scales[key]).x}
    //   });
    //   return scales;
    // }



    const xScales = Object.keys(scales).map(key => { //for every scale in scales data, generate an xScale
      const thisScale = scales[key];
      return ({
        name: key,
        scale: generateScales(thisScale).x,
        yPos: thisScale.yPos,
        units: thisScale.units
      })
    });

    /*
      xScales(generateScales(scaleLinear(scale)))
      - take xScale and rescale according to transformations (newXScale)
      - update each array entry's domain with that of newXScale
    */
    //if there is a zoom transform, override each xScales.scale with adjusted scale
    if (currentZoomState) {
      xScales.forEach((thisScale, i) => {
        const newXScale = currentZoomState.rescaleX(thisScale.scale); //zoom state comes w methods for transformations
        xScales[i].scale = newXScale; //sets domain to newXScale domain

      });


    };
    // const xScale = scaleLinear()
    //   .domain([xMin, xMax]) //scaleLinear takes domain (data values) & maps onto range (screen pixel values)
    //   .range([0, dimensions.width]);
