      //if difference in y vaules greater than 5, resize to min and max
      .domain((rangeY>breakpoint) ? [yMin, yMax] : [yMin-breakpoint, yMax+breakpoint])
      .range([0, dimensions.height]);
    //inverse scales for drag behavior
    // const xScaleInverse = scaleLinear()
    //   .domain([-dimensions.width, dimensions.width])
    //   .range([-rangeX, rangeX]); //maxRangeX-minRangeX gives section of x values displayed onscreen
    const yScaleInverse = scaleLinear()
      .domain([-dimensions.height, dimensions.height])
      .range((rangeY>breakpoint) ? [-rangeY, rangeY] : [-breakpoint*5, breakpoint*5]);





    //---draw intervals---//
    if (intervals.length) {
      svg
      .selectAll(".intervals")
      .data(intervals)
      .join("rect")
      .attr("class", "intervals")
      .attr("x", d => xScales.find(n => n.name === d.scale).scale(d.start))
      // .attr("x", n => generateScales(scales[n.scale]).x(n.start))
      // .attr("y", n => n.yStart ? yScale(n.yStart) : yScale(minMax[2]))
      .attr("y", n => n.yStart ? yScale(n.yStart) : 0) //todo
      .attr("width", d => xScales.find(n => n.name === d.scale).scale(d.end) - xScales.find(n => n.name === d.scale).scale(d.start))
      .attr("height", n => {
        if (n.yStart & n.yEnd) {
          return (yScale(n.yEnd)-yScale(n.yStart))
        } else if (n.yStart) {
          return (yScale(yMax+10)-yScale(n.yStart))
        } else {
          return (yScale(yMax+10)) //todo
          // return (yScale(minMax[3])-yScale(minMax[2]))
        }})
      .attr("opacity", n => n.opacity ? n.opacity : 0.5)
      .attr("fill", n => n.color ? n.color : "#999")
      .attr("stroke", "black") //todo
      .attr("stroke-width", n => n.importance ? n.importance*2 : 0)
      .on("click", (e, target) => {
        //displays interval properties when clicked
        if (toggle==="certainty" || toggle==="importance") {
          setInflectTarget(target);
        } else {
          setInfo({position: [e.x, e.y], target: target, type: "interval"});  //todo: set info to null when clicking again
        }
        e.stopPropagation();
      });
    }

    //---draw bottom axis---//

    xScales.forEach(thisScale => {
      const xAxis = axisBottom(thisScale.scale)
      .tickFormat(format(''));

      svg
        .append("g") //todo
        .attr("class", "remove")
        .style("transform", `translateY(${dimensions.height*thisScale.yPos}px)`)
        .call(xAxis)
        .raise()
        .call(g => g.select(".domain") //removes solid black axis bar
          .remove())
        .call(g => g.selectAll(".tick line") //styles individual ticks
          .attr("stroke-opacity", 0.5))
        .call(g => g.selectAll(".tick text")
          .attr("y", 10) //todo?
          .attr("opacity", 0.5));
      svg
        .append("text")
        .text(thisScale.units)
        .attr("class", "axisLabel smallTxt remove")
        .attr("x", dimensions.width/2)
        .attr("y", dimensions.height*(thisScale.yPos+.07))
        .attr("text-anchor", "middle")
        .attr("opacity", 0.5);
      }
    );


    // const xAxis = axisBottom(generateScales(scales.smalls).x)
    //   // .tickSize(-dimensions.height) //tick extends to entire height of svg
    //   .tickValues(logic ? [-1, 0, 1] : null)
    //   .tickFormat(logic ? x => scaleOrdinal([-1, 0, 1], ["before", "now", "after"])(x) : format('')); //gets rid of , for 1000s groupings

    // svg
    //   .select(".x-axis") //selects and replaces .x-axis
    //   .style("transform", `translateY(${dimensions.height}px)`)
    //   .call(xAxis)
    //   .raise()
      // .call(g => g.select(".domain") //removes solid black axis bar
      //   .remove())
      // .call(g => g.selectAll(".tick line") //styles individual ticks
      //   .attr("stroke-opacity", 0.5))
      //   // .attr("stroke-dasharray", "2,2"))
      // .call(g => g.selectAll(".tick text")
      //   .attr("y", 10) //todo?
      //   .attr("opacity", 0.5));
      //   // .attr("font-size", fontSize*.8)); //replace class "x-axis" w xAxis
    //

    //--drag behavior--//
    const dragged = drag()
      // .on("start", e => {
      //   select(this).attr("stroke", "black");
      // })
      .on("drag", e => {
        //todo (round new position to .01)
        const dx = generateScales(scales[e.subject.scale]).xInverse(e.dx);
        const dy = Math.round(yScaleInverse(e.dy)*100)/100;
        const newNodes = [...data]; //always make a copy when updating array
        //find array element in data with matching ID and set new x y coordinates
        newNodes.find(n => n.id === e.subject.id).x += dx;
        newNodes.find(n => n.id === e.subject.id).y += dy;
        setData(newNodes);
      });

    //---draw links---//
    const linker = linkHorizontal()
      .source(link => link.source)
      .target(link => link.target)
      // .x(n => generateScales(scales[n.scale]).x(logic ? logicScale(n.x) : n.x))
      .x(d => xScales.find(n => n.name === d.scale).scale(d.x))
      .y(d => yScale(d.y));

    svg
      .selectAll(".link")
      .data(links)
      .join("path")
      .raise()
      .attr("class", "link pointer")
      .attr("d", linker)
      .attr("fill", "none")
      .attr("stroke", n => n.color? n.color : "black")
      .attr("stroke-width", n => n.width ? n.width : radius/3)
      .attr("stroke-opacity", n => n.opacity ? n.opacity : 1)
      .attr("stroke-dasharray", n => n.dashed ? n.dashed : null)
      .on("click", (e, target) => {
        // if (toggle==="causality") {
        //   //must select at least 2 temporal objects
        //
        // } else if (toggle==="foreshadowing") {
        //   if (!target.target) { //if target is an endpoint, indeterminate end
        //
        //
        //   } else { //otherwise, change mouse to no symbol
        //
        //   }
        //   //option to adjust gradient eventually
        // } else
        if (toggle==="certainty" || toggle==="importance") {
          setInflectTarget(target);
        } else {
          setInfo({position: [e.x, e.y], target: target, type: "link"}); //displays line properties when clicked
        }
        e.stopPropagation();
      });


    //---draw points---//
    svg
      .selectAll(".node")
      .data(activeData.filter(d => !d.foreshadowing && !d.cancelled))
      .join("circle")
      .raise()
      .attr("class", "node pointer")
      .attr("r", n => n.radius? n.radius : radius) //todo for accessibility
      .attr("fill", d => d.color ? d.color : "black") //if it has a color other than black, use that
      .attr("opacity", d => d.opacity ? d.opacity : 1)
      // .attr("cx", d => generateScales(scales[d.scale]).x(logic ? logicScale(d.x) : d.x))
      .attr("cx", d => xScales.find(n => n.name === d.scale).scale(d.x)) //todo
      .attr("cy", d => yScale(d.y))
      .call(dragged)
      .on("click", (e, target) => {
        if (toggle==="cancelled") {
          const newNodes = [...data];
          const node = newNodes.find(n => n.id === target.id);
          node.cancelled = true;
          setData(newNodes);
        } else if (toggle==="connection") {
          const newOrder = [...syntacticOrder];
          switch (syntacticOrder[2]) {
            case 0: //selecting 2nd point
              newOrder[1] = target;
              newOrder[2] = 1;
              break;
            default: //selecting 1st point
              newOrder[0] = target;
              newOrder[2] = 0;
          }
          console.log(newOrder);
          setSyntacticOrder(newOrder);
        } else if (toggle==="foreshadowing") { //todo: adjust gradient
          if (!target.target) { //if target is an endpoint, indeterminate end
            const newNodes = [...data];
            const node = newNodes.find(n => n.id === target.id);
            node.foreshadowing = true;
            setData(newNodes);
          } else { //otherwise, change mouse to no symbol
            console.log("forbidden");
          }
        } else if (toggle==="certainty" || toggle==="importance"){
          setInflectTarget(target);
        } else {
          setInfo({position: [e.x, e.y], target: target}); //displays node properties when clicked
        }
        e.stopPropagation(); //stops deselect when bg is clicked
      });

    //---draw inflections---//

    //--draw connetion inflection--//
    //circle around point
    svg
      .selectAll(".connections")
      .data(activeData.filter(d => d.connections))
      .join("circle")
      .raise()
      .attr("class", "connections pointer")
      .attr("r", n => n.radius? n.radius*1.5 : radius*2) //todo for accessibility
      .attr("fill", "none")
      .attr("stroke", "black")
      .attr("stroke-dasharray", "3")
      // .attr("opacity", d => d.opacity ? d.opacity : 1)
      .attr("cx", d => xScales.find(n => n.name === d.scale).scale(d.x)) //todo
      .attr("cy", d => yScale(d.y));

    //link connecting circles
    const ctLinker = linkHorizontal()
      .source(node => data.find(x => x.id === node.id))
      .target(node => data.find(x => x.id === node.connections[0]))
      .x(d => xScales.find(n => n.name === d.scale).scale(d.x))
      .y(d => yScale(d.y));
    svg
      .selectAll(".connectionLinks")
      .data(activeData.filter(n => n.connections && !n.connections[1]))
      .join("path")
      .raise()
      .attr("class", "connectionLinks pointer")
      .attr("d", ctLinker)
      .attr("fill", "none")
      .attr("stroke", "black")
      // .attr("stroke", n => n.color? n.color : "black")
      // .attr("stroke-opacity", n => n.opacity ? n.opacity : 1)
      .attr("stroke-dasharray", "3")

    //initialize custom symbols
    const cancelledSymbol = {
      draw: (context, size) => {
        let s = Math.sqrt(size)/2;
            context.moveTo(s,0);
            context.lineTo(s*2,s);
            context.lineTo(s,s*2);
            context.lineTo(0,s);
            context.lineTo(-s,s*2);
            context.lineTo(-s*2,s);
            context.lineTo(-s,0);
            context.lineTo(-s*2,-s);
            context.lineTo(-s,-s*2);
            context.lineTo(0,-s);
            context.lineTo(s,-s*2);
            context.lineTo(s*2,-s);
            context.closePath();
      }
    };
    //foreshadowing symbol
    const fsSymbol = {
      draw: (context, size) => {
        let s = size;
            context.moveTo(0,0);
            context.lineTo(s*2,s/2);
            context.lineTo(s*2,-s/2);
            context.closePath();
      }
    };
    //foreshadowing gradient
    var fsg = svg.append("defs").append("linearGradient")
    .attr("id", "fsgradient")//id of the gradient
    .attr("x1", "0%")
    .attr("x2", "100%") //since its a horizontal linear gradient
    .attr("y1", "0%")
    .attr("y2", "0%");

    fsg.append("stop")
    .attr("offset", "0%")
    .style("stop-color", "black")//start in black. TODO
    .style("stop-opacity", 1);

    fsg.append("stop")
    .attr("offset", "100%")
    .style("stop-color", "black")//end in transparent
    .style("stop-opacity", 0);

    //---draw symbols---//
    //todo: doesn't work when cancelled is true from the start
    svg
      .selectAll(".cancelled")
      .data(activeData.filter(d => d.cancelled))
      .join("path")
      .raise()
      .attr("class", "cancelled pointer")
      .attr("d", symbol(cancelledSymbol, radius*radius*5))
      .attr("fill", d => d.color ? d.color : "black") //if it has a color other than black, use that
      .attr("transform", d => `translate(${xScales.find(n => n.name === d.scale).scale(d.x)}, ${yScale(d.y)})`)
      .on("click", (e, target) => {
        if (toggle==="cancelled") {
          const newNodes = [...data];
          const node = newNodes.find(n => n.id === target.id);
          delete node.cancelled;
          setData(newNodes);
        } else {
          setInfo({position: [e.x, e.y], target: target}); //displays node properties when clicked
        }
        e.stopPropagation();
      });

    //---draw foreshadowing---//
    svg
      .selectAll(".foreshadowing")
      .data(activeData.filter(d => d.foreshadowing))
      .join("path")
      .raise()
      .attr("class", "foreshadowing pointer")
      .attr("d", symbol(fsSymbol, radius*radius*5))
      .style("fill", "url(#fsgradient)")
      .attr("transform", d => `translate(${xScales.find(n => n.name === d.scale).scale(d.x)}, ${yScale(d.y)})`)
      .on("click", (e, target) => {
        if (toggle==="foreshadowing") {
          const newNodes = [...data];
          const node = newNodes.find(n => n.id === target.id);
          delete node.foreshadowing;
          setData(newNodes);
        } else {
          setInfo({position: [e.x, e.y], target: target}); //displays node properties when clicked
        }
        e.stopPropagation();
      });

    //---draw labels---//
    svg
      .selectAll(".label")
      .data(activeData)
      .join("text")
      .raise()
      .attr("class", "label")
      .text(node => showLabel ? node.name : node.id)
      .attr("x", d => xScales.find(n => n.name === d.scale).scale(d.x))
      .attr("y", d => yScale(d.y + (rangeY>breakpoint ? rangeY*.06 : breakpoint/5))) //vertical offset. see scales
      .attr("text-anchor", "middle");
      // .attr("font-size", fontSize);


    //---zoom---//
    const zoomBehavior = zoom()
      .scaleExtent([0.25, 5]) //can zoom 2x smaller and 5x bigger
      .translateExtent([[-100, 0], [dimensions.width+100, dimensions.height]]) //limits how far graph can pan
      .on("zoom", e => {
        const zoomState = e.transform; //returns zoom transformations as an object
        setCurrentZoomState(zoomState);
      });

    svg.on("click", () => setInfo(null));
    svg.call(zoomBehavior);

  }, [data, setData, intervals, scales, toggle, activeLayer, activeData, dimensions, logic, currentZoomState, setInfo, radius, showLabel]);

  return (
    <div ref={wrapperRef} className="svgWrapper">
      <svg id="graphic" ref={svgRef}>
      </svg>
    </div>
  );
};


export default Graphic;
