import React, { useState } from 'react';
import './stylesheets/index.scss';
import Data from "./history.json";
import Graphic from "./components/Graphic";
import Image from "./components/Image";
import NewPointForm from "./components/NewPointForm";
import InfoBox from "./components/InfoBox";
import ExportDataForm from "./components/ExportDataForm";
// import findMinMax from "./components/findMinMax";



function ToolsBar(props) {
  const [toggleLabel, setToggleLabel] = useState(true);

  const handleClick = (e) => {
    switch (e.target.id) {
      case 'toggleLabel':
        toggleLabel ?
          setToggleLabel(false)
        : setToggleLabel(true);
      break
      case 'addTemporalObject':

      break
      // case 'save': //save JSON as file to computer
      //   // console.log(this.props.src);
      //   const fileData = JSON.stringify(this.props.src);
      //   const blob = new Blob([fileData], {type: "text/plain"});
      //   const url = URL.createObjectURL(blob);
      //   const link = document.createElement('a');
      //   link.download = `data.json`;
      //   link.href = url;
      //   link.click();
      // break
      default:
        props.setToggle(e.target.id);
        console.log(props.data);
    }
    e.preventDefault();
  }

  return (
    <>
      <nav className="ui fullWidth">
        <ul className="inline">
          <li><button id="addTemporalObject" onClick={handleClick}>Add Temporal Object</button></li>
          <li><button id="toggleLabel" onClick={handleClick}>{toggleLabel ? "Toggle IDs" : "Toggle Labels"}</button></li>
          <li><button id="settings" onClick={handleClick}>Settings</button></li>
          <li><button id="import" onClick={handleClick}>Import</button></li>
          <li><button id="save" onClick={handleClick}>Save & Export</button></li>
        </ul>
      </nav>
      <div className="ui absolute">
        hi
      </div>
    </>
  );
}


function App() {

  //blank ChronoJSON template
  const defaultJSON = {
    "imgs": [],
    "nodes":
    [
      []
    ],
    "intervals":
    []
  };

  //user defined
  const radius = 5;

  //for the entire ChronoJSON
  const [src, setSrc] = useState(Data);

  //for ChronoJSON history
  const [history, setHistory] = useState([])

  //for toggling the active dataset
  const [activeData, setActiveData] = useState(0);
  //pertaining to datapoints only
  const [data, setData] = useState(src.nodes[activeData]); //todo: comparative timelines, layering
  //pertaining to intervals only
  const [intervals, setIntervals] = useState(src.intervals);
  //UI window toggles
  const [toggle, setToggle] = useState();
  //UI info boxes for points, lines, etc.
  const [info, setInfo] = useState();

  /*
    TODO:
    - findMinMax() adjust less frequently. ie: if new data can be panned to, no need to adjust viewport
    - for piecewise timeline, make function that generates different graphics
    - create a master array of all snapshots of data (for scrubber)
  */

  return (
    <>
      <ToolsBar setToggle={setToggle} data={data}/>
      {toggle==="addPoint" ? <NewPointForm data={data} setData={setData} setToggle={setToggle}/> : null}
      {toggle==="save" ? <ExportDataForm src={src} data={data} activeData={activeData}/> : null}
      <div id="main" className="container fullHeight">
        {data.length ?
          <Graphic
            data={data}
            setData={setData}
            intervals={intervals}
            setIntervals={setIntervals}
            radius={radius}
            setInfo={setInfo}
          />
          : <div className="flexCenter">Upload data or <button onClick={() => setToggle("addPoint")}>Add new point</button></div>
        }
        {src.imgs.length ? <Image urls={src.imgs}/> : null}
      </div>
      {info ? <InfoBox info={info} data={data} setData={setData}/> : null}
    </>
  );
}

export default App;
