import React, { useState } from 'react';

/*
  UPDATE NEEDED
  For saving and downloading JSON data onto computer.
*/

function ExportDataForm({src, data, activeData, setToggle}) {
  const [fileName, setFileName] = useState("");

  const handleSave = (e) => {
    e.preventDefault();
    //syncs changes in nodes data with src
    const newSrc = src;
    newSrc.nodes[activeData] = data; //TODO: update to be compatible w/ new data structure

    //saves synced src as a file
    const fileData = JSON.stringify(newSrc);
    const blob = new Blob([fileData], {type: "text/plain"});
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.download = `${fileName}.json`;
    link.href = url;
    link.click();


  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center">
      <button className="right" onClick={handleClose}>x</button><br/>
      <label>
        File Name <span className="redText">* </span>:
        <input
          type="text"
          value={fileName}
          onChange={(e) => setFileName(e.target.value)}
          required
        />
      </label><br/><br/>
      <input type="submit" value="Save Data"/>
    </form>
  );
}

export default ExportDataForm;
