import React, { useState } from 'react';
import InputFormText from './InputFormText';
import InputFormNum from './InputFormNum';

/*
   UI and function for adding new scales.
   Eventually split into 2 different components: NewXScaleForm and NewYScaleForm
*/


function NewScaleForm({src, updateSrc, setToggle, setActiveScale}) {

  //values for form, mutable by onChange
  const [values, setValues] = useState({
    //for x scale
    xName:"",
    xMin:"",
    xMax:"",
    xLabel:"",
    xYPos:"",
    xTickValues:"",
    xHideLabel:false,
    xSegmentsNum:0,
    //for y scale
    yName:"",
    yMin:"",
    yMax:"",
    yLabel:"",
    yHideLabel:false
  });

  const handleChange = (e) => {
    e.preventDefault();
    const name = e.target.name;
    const value = e.target.type==="checkbox"?e.target.checked:e.target.value;
    // const { value, name } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    const newScales = {...src.scales};

    const tempxMin = parseFloat(values.xMin);
    const tempxMax = parseFloat(values.xMax);
    const tempYPos = parseFloat(values.xYPos); //for x scale
    const tempyMin = parseFloat(values.yMin);
    const tempyMax = parseFloat(values.yMax);

    if (tempxMin>=tempxMax) {
      alert("Minimum Value must be less than or equal to Maximum Value!");
      return;
    } else if (tempYPos>1) {
      alert("Vertical Position must be between 0 (top) and 1 (bottom)!");
      return;
    } //else if (scale name already exists) {
    //
    // };

    //generate new x scale
    newScales.x[values.xName] = {
      "min": tempxMin,
      "max": tempxMax,
      "label": values.xLabel,
      "yPos": tempYPos,
      "hideLabel":values.xHideLabel,
      "segments": [] //todo
    }
    newScales.y[values.yName] = {
      "min": tempyMin,
      "max": tempyMax,
      "label": values.yLabel
    }

    updateSrc(newScales, "scales");
    setActiveScale(values.xName); //move to updateSrc
    setToggle(null);
  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center container">
      <span className="textSecondary"><b>x scale</b></span><br/>
      <InputFormText
        label="Name"
        name="xName"
        value={values.xName}
        handleChange={handleChange}
        required={true}
      />
      <InputFormNum
        label="Minimum Value"
        name="xMin"
        placeholder="#s for now"
        value={values.xMin}
        handleChange={handleChange}
        required={true}
      />
      <label className="textSecondary">
        Maximum Value<span className="redText">* </span>
        <input type="text"
          placeholder="#s for now"
          name="xMax"
          value={values.xMax}
          onChange={handleChange}
          required
        />
      </label>
      <label className="textSecondary">
        Units Label<span className="redText">* </span>
        <input type="text"
          placeholder="ie: Years"
          name="xLabel"
          value={values.xLabel}
          onChange={handleChange}
          required
        />
      </label>
      <label className="textSecondary">
        Hide Label?
        <input type="checkbox"
          name="xHideLabel"
          value={values.xHideLabel}
          onChange={handleChange}
        />
      </label>
      <label className="textSecondary">
        Vertical Position<span className="redText">* </span>
        <input
          type="text"
          placeholder="0 (top) - 1 (bottom)"
          name="xYPos"
          value={values.xYPos}
          onChange={handleChange}
          required
        />
      </label>
      <label className="textSecondary">
        Segments
        <input
          type="number"
          placeholder="granularities within scale"
          name="xSegmentsNum"
          value={values.xSegmentsNum}
          onChange={handleChange}
        />
      </label><br/>
      <span className="textSecondary"><b>y scale</b></span><br/>
      <label className="textSecondary">
        Name<span className="redText">* </span>
        <input type="text"
          placeholder="any unicode characters"
          name="yName"
          value={values.yName}
          onChange={handleChange}
          required
        />
      </label>
      <label className="textSecondary">
        Minimum Value<span className="redText">* </span>
        <input type="text"
          placeholder="#s for now"
          name="yMin"
          value={values.yMin}
          onChange={handleChange}
          required
        />
      </label>
      <label className="textSecondary">
        Maximum Value<span className="redText">* </span>
        <input type="text"
          placeholder="#s for now"
          name="yMax"
          value={values.yMax}
          onChange={handleChange}
          required
        />
      </label>
      <label className="textSecondary">
        Units Label<span className="redText">* </span>
        <input type="text"
          placeholder="ie: Years"
          name="yLabel"
          value={values.yLabel}
          onChange={handleChange}
          required
        />
      </label><br/>
      <div className="flexCenter">
        <button className="buttonSecondary" onClick={handleClose}>Cancel</button> &nbsp;
        <button className="buttonSecondary" onClick={handleSubmit}>Submit</button>
      </div>
    </form>
  )
};

export default NewScaleForm;
