import React, { useState, useEffect, useRef } from 'react';
import { select, scaleLinear, drag, axisBottom } from "d3";
import Canvas from './components/Canvas'


function App() {
  // const [data, setData] = React.useState(null);
  //
  // //make a GET request to backend using Fetch API
  // //store returned JSON in state variable "data"
  // React.useEffect(() => {
  //   fetch("/api")
  //     .then((res) => res.json())
  //     .then((data) => setData(data.message));
  // }, []);
  //
  // //draw circle
  // const draw = (ctx, frameCount) => {
  //     ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
  //     ctx.fillStyle = '#000000'
  //     ctx.beginPath()
  //     ctx.arc(50, 100, 20*Math.sin(frameCount*0.05)**2, 0, 2*Math.PI)
  //     ctx.fill()
  //   }
  //
  // //if data not there yet, show "Loading..."
  // return (
  //   <>
  //     <p>{!data ? "Loading..." : data}</p>
  //     <Canvas draw={draw}/>
  //   </>
  // );
  const [data, setData] = useState([25, 30, 45, 60, 20]);

  const radius = 4;
  //reference object to pass to svg in React Dom
  const svgRef = useRef();
  const wrapperRef = useRef(); //react has trouble getting dimensions within react
  // const { width, height } =
  //   dimensions || wrapperRef.current.getBoundingClientRect();
  //
  // //lines and axes
  // //mapping xValues to width of wrapper
  // const xScale = scaleLinear()
  //     .domain([0, max(data)])
  //     .range([0, width]);

  //all d3 shennanigans goes in this hook. called initially and on every data change
  useEffect(()=> {
    const svgContent = select(svgRef.current);
    const xScale = scaleLinear()
        .domain([0, 60])
        .range([0, 1000]);
    const xAxis = axisBottom(xScale);
    //find element marked x-axis and replace it w xAxis
    svgContent.select(".x-axis").call(xAxis);

    svgContent
      .selectAll("circle")
      .data(data)
      .join("circle")
      .attr("r", radius)
      .attr("cx", d => xScale(d))
      .attr("cy", d => xScale(d)*2);

  }, [data]);

  return (
    <div ref={wrapperRef}>
      <svg ref={svgRef}>
        <g className="x-axis" />
      </svg>
    </div>
  );
}

export default App;
