import React, {useEffect, useRef} from 'react';
import HandsFree from 'handsfree';
// import hands from '@mediapipe/hands';

//https://handsfree.js.org

function Hands(props) {
  //init
  const handsFree = new HandsFree({
    hands:{
      enabled: true,
      maxNumHands: 2
    }
  });
  handsFree.enablePlugins('browser');
  handsFree.useGesture({
  "name": "horns",
  "algorithm": "fingerpose",
  "models": "hands",
  "confidence": 7.5,
  "description": [
    [
      "addCurl",
      "Thumb",
      "NoCurl",
      1
    ],
    [
      "addCurl",
      "Thumb",
      "HalfCurl",
      0.16666666666666666
    ],
    [
      "addDirection",
      "Thumb",
      "DiagonalUpRight",
      1
    ],
    [
      "addDirection",
      "Thumb",
      "VerticalUp",
      0.08333333333333333
    ],
    [
      "addDirection",
      "Thumb",
      "DiagonalUpLeft",
      0.6666666666666666
    ],
    [
      "addCurl",
      "Index",
      "NoCurl",
      1
    ],
    [
      "addCurl",
      "Index",
      "HalfCurl",
      0.05
    ],
    [
      "addDirection",
      "Index",
      "DiagonalUpRight",
      0.8181818181818182
    ],
    [
      "addDirection",
      "Index",
      "VerticalUp",
      0.09090909090909091
    ],
    [
      "addDirection",
      "Index",
      "DiagonalUpLeft",
      1
    ],
    [
      "addCurl",
      "Middle",
      "FullCurl",
      1
    ],
    [
      "addDirection",
      "Middle",
      "VerticalUp",
      0.6
    ],
    [
      "addDirection",
      "Middle",
      "HorizontalRight",
      0.1
    ],
    [
      "addDirection",
      "Middle",
      "DiagonalUpRight",
      0.4
    ],
    [
      "addDirection",
      "Middle",
      "DiagonalUpLeft",
      1
    ],
    [
      "addCurl",
      "Ring",
      "FullCurl",
      1
    ],
    [
      "addCurl",
      "Ring",
      "HalfCurl",
      0.05
    ],
    [
      "addDirection",
      "Ring",
      "DiagonalUpRight",
      1
    ],
    [
      "addDirection",
      "Ring",
      "VerticalUp",
      0.5
    ],
    [
      "addDirection",
      "Ring",
      "DiagonalUpLeft",
      0.6
    ],
    [
      "addCurl",
      "Pinky",
      "NoCurl",
      1
    ],
    [
      "addDirection",
      "Pinky",
      "VerticalUp",
      1
    ],
    [
      "addDirection",
      "Pinky",
      "DiagonalUpLeft",
      0.05
    ],
    [
      "setWeight",
      "Index",
      2
    ],
    [
      "setWeight",
      "Pinky",
      2
    ]
  ],
  "enabled": true
})
  const ref = useRef();

//only runs on component mount & dismount
  useEffect(() => {
    console.log(handsFree);
    handsFree.start(()=>{handsFree.pause()}); //initializes loop but pauses it instantly'
    // console.log(handsFree.pause);
    //stop webcam on unmount
    return () => handsFree.stop();
  }, []);

  const handleClick = (e) => {
    e.preventDefault();
    if (!props.enabled){
      handsFree.unpause();
      props.setEnabled(true);
      console.log(handsFree.isLooping);

    } else {
      handsFree.pause()
      console.log(handsFree.isLooping);
      props.setEnabled(false);
    }
  }

  //all the magic happens here
  if (props.enabled) {
    handsFree.on('data', (e) => {
      console.log(e.hands?e.hands.gesture:null);

    });
  }

  /*
    TODO:
    - draw palm Positions
      - shrink for click
      - something for drag (oblong?)
    - add "click" plugin
      - create plugin for "horns" hand gesture
      - on horns, new MouseEvent
    - add "pause" plugin
      - on fist, pause/stop handsFree

      {props.enabled &&
        <div>
        </div>
      }
  */


  return (
    <>
      {/*<HandsButton handleClick={handleClick} />*/}
      <button onClick={handleClick}>
        {props.enabled?"disable hands":"enable hands"}
      </button>
    </>
  )
};

export default Hands;
