import React, { useState, useEffect, useRef } from 'react';
import { select, scaleLinear, axisBottom, linkHorizontal, format, drag, zoom } from "d3";
import useResizeObserver from "./useResizeObserver";
import generateLinks from "./generateLinks";

function Graphic({data, setData, minMax, radius, fontSize}) {

  //all data is passed down from and synced to App.js

  //initialize reference object to pass to svg in React Dom
  const svgRef = useRef();
  //svg wrapped in div bc otherwise resize observer callback won't work
  const wrapperRef = useRef();
  const dimensions = useResizeObserver(wrapperRef);
  const [currentZoomState, setCurrentZoomState] = useState();

  //initialize links between nodes
  const links = generateLinks(data);

  //initialize bottom axis label
  const [axisLabel, setAxisLabel] = useState("Years (AD)");

  //all d3 shennanigans goes in this hook. called initially and on every data change
  useEffect(() => {
    const svg = select(svgRef.current); //"svg" is the current version of svgRef data
    //prevents crashing when useResizeObserver returns null before svg is rendered
    if (!dimensions) return;
    //prevents crashing before min and max are calculated
    if (!minMax) return;
    //prevents crashing before links are calculated
    if (!links) return;

    //---scales---//
    //scaleLinear takes domain (data values) & maps onto range (screen pixel values)
    const xScale = scaleLinear()
      .domain([minMax[0], minMax[1]]) //0 is minX, 1 is maxX (reference findMinMax)
      .range([0, dimensions.width]);
    const yScale = scaleLinear()
      .domain([0, minMax[3]]) //todo
      .range([0, dimensions.height]);
    //inverse scales for drag behavior
    const xScaleInverse = scaleLinear()
      .domain([-dimensions.width, dimensions.width])
      .range([-(minMax[1]-minMax[0]), minMax[1]-minMax[0]]); //minMax[1]-minMax[0] gives section of x values displayed onscreen
    const yScaleInverse = scaleLinear()
      .domain([-dimensions.height, dimensions.height])
      .range([-minMax[3], minMax[3]]); //todo

    if (currentZoomState) {
      const newXScale = currentZoomState.rescaleX(xScale); //zoom state comes w methods for transformations
      xScale.domain(newXScale.domain());
      xScaleInverse.range([
        -(newXScale.domain()[1]-newXScale.domain()[0]),
        (newXScale.domain()[1]-newXScale.domain()[0])
      ]);
      svg.selectAll(".axisLabel").remove(); //avoids compounding axis labels
    };

    //---draw bottom axis---//
    const xAxis = axisBottom(xScale)
      .tickSize(-dimensions.height) //tick extends to entire height of svg
      .tickFormat(format('')); //gets rid of , for 1000s groupings

    svg
      .select(".x-axis") //selects and replaces .x-axis
      .style("transform", `translateY(${dimensions.height}px)`)
      .call(xAxis)
      .call(g => g.select(".domain") //removes solid black axis bar
        .remove())
      .call(g => g.selectAll(".tick line") //styles individual ticks
        .attr("stroke-opacity", 0.5)
        .attr("stroke-dasharray", "2,2"))
      .call(g => g.selectAll(".tick text")
        .attr("y", 10) //todo?
        .attr("opacity", 0.5));
        // .attr("font-size", fontSize*.8)); //replace class "x-axis" w xAxis

    svg
      // .selectAll(".axislabel")
      // .data([1])
      // .join("text")
      .append("text")
      .text(axisLabel)
      .attr("class", "axisLabel smallTxt")
      .attr("x", dimensions.width/2)
      .attr("y", dimensions.height*1.08)
      .attr("text-anchor", "middle")
      .attr("opacity", 0.5); //todo
      // .attr("font-size", fontSize*.8);

    //--drag behavior--//
    const dragged = drag()
      // .on("start", e => {
      //   select(this).attr("stroke", "black");
      // })
      .on("drag", e => {
        const newNodes = [...data]; //always make a copy when updating array
        //find array element in newData (id = index) and set new x y coordinates
        //define id as array.length-1
        newNodes[e.subject.id].x += xScaleInverse(e.dx);
        newNodes[e.subject.id].y += yScaleInverse(e.dy);
        // const newSrc = Object.assign({}, src, {nodes: newNodes});
        setData(newNodes);
      });

    //---draw lines---//
    const linker = linkHorizontal()
      .source(link => link.source)
      .target(link => link.target)
      .x(node => xScale(node.x))
      .y(node => yScale(node.y));

    svg
      .selectAll(".link")
      .data(links)
      .join("path")
      .attr("class", "link")
      .attr("d", linker)
      .attr("fill", "none")
      .attr("stroke", n => n.color? n.color : "black")
      .on("click", e => {
        console.log(e);
      });


    //---draw points---//
    svg
      .selectAll(".node")
      .data(data)
      .join("circle")
      .attr("class", "node")
      .attr("r", radius)
      .attr("fill", d => d.color ? d.color : "black") //if it has a color other than black, use that
      .attr("cx", d => xScale(d.x))
      .attr("cy", d => yScale(d.y))
      .call(dragged)
      .on("click", (e, target) => {
        window.alert(
`ID: ${target.id}
Label: ${target.name}
Time: ${target.x}
y Position: ${target.y}
Target(s): ${target.target}
Color: ${target.color ? target.color : "Black"}`
        );
        svg
          // .select(".info")
          // .style("left", e.x)
          // .style("top", e.y)
          .append("rect")
          .attr("x", e.x)
          .attr("y", e.y)
          .attr("fill", "white")
          .attr("opacity", "90%")
          .attr("width", 100)
          .attr("height", 50)
      });


    //---draw labels---//
    svg
      .selectAll(".label")
      .data(data)
      .join("text")
      .attr("class", "label")
      .text(node => node.name)
      .attr("x", node => xScale(node.x))
      .attr("y", node => yScale(node.y + 2))
      .attr("text-anchor", "middle");
      // .attr("font-size", fontSize);

    // //--draw info pop-up--//
    // svg
    //   .selectAll(".info")
    //   .data(e.x, e.y)
    //   .join("square")
    //   .

    //---zoom---//
    const zoomBehavior = zoom()
      .scaleExtent([0.25, 5]) //can zoom 2x smaller and 5x bigger
      .translateExtent([[-100, 0], [dimensions.width+100, dimensions.height]]) //limits how far graph can pan
      .on("zoom", e => {
        const zoomState = e.transform; //returns zoom transformations as an object
        setCurrentZoomState(zoomState);
      });

    svg.call(zoomBehavior);
  }, [data, dimensions, currentZoomState, minMax, radius, fontSize]);

  return (
    <div ref={wrapperRef}>
      <svg ref={svgRef}>
        <div className="info"></div>
        <g className="x-axis" />
      </svg>
    </div>
  );
};


export default Graphic;
