import React, { useState } from 'react';
import './stylesheets/index.scss';
import Data from "./data.json";
import Graphic from "./components/Graphic";
import Image from "./components/Image";
import NewInstantForm from "./components/NewInstantForm";
import NewZoneForm from "./components/NewZoneForm";
import InfoBox from "./components/InfoBox";
import ExportDataForm from "./components/ExportDataForm";
// import ImportDataForm from "./components/ImportDataForm";
import ToolsBar from "./components/ToolsBar";
import Layers from "./components/Layers";
import Scrubber from "./components/Scrubber"
import Legend from "./components/Legend"
import generateLinks from "./components/generateLinks";





function SelectedDisplay(props){
  if (!props.target) { //no selection
    return <p>Selected: None</p>;
  } else if (props.target.source) { //link
    return <p>Selected: {props.target.source.name} ({props.target.source.id}) – {props.target.target.name} ({props.target.target.id})</p>;
  } else if (props.target.start) { //zone
    return <p>Selected: {props.target.name} ({props.target.start} - {props.target.end})</p>;
  } else { //instant
    return <p>Selected: {props.target.name} ({props.target.id})</p>;
  }
};

function Certainty(props) {
  // console.log(props.target);

  const [input, setInput] = useState();

  //set opacity of target
  const handleSubmit = (e) => {
    e.preventDefault();

    if (props.target.start) { //zone
      const newZones = [...props.zones];
      const zone = newZones.find(n => n.id === props.target.id);
      zone.opacity = parseFloat(input)*.5;
      props.setZones(newZones);
      return;
    }

    const newInstants = [...props.data];

    if (props.target.source) { //link
      const instant = newInstants.find(n => n.id === props.target.source.id);
      instant.intervalOpacity = parseFloat(input);
    } else { //instant
      const instant = newInstants.find(n => n.id === props.target.id);
      instant.intervalOpacity = instant.opacity ? instant.opacity : 1; //don't change interval opacity
      instant.opacity = parseFloat(input);
    }
    props.setData(newInstants);
  };

  const handleClose = (e) => {
    props.setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <SelectedDisplay target={props.target}/>
      <label>
        Certainty
        <input type="text"
          placeholder="0 (never) - 1 (certain)"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          required
        />
      </label><br/>
      <input type="submit" value="Submit"/>
    </form>
  )
}

function Importance(props) { //TODO: intervals, negatives, "steps"

  const [input, setInput] = useState();

  //todo: make for interval also
  const handleSubmit = (e) => {
    e.preventDefault();

    if (props.target.start) { //zone
      const newZones = [...props.Zones];
      const zone = newZones.find(n => n.id === props.target.id);
      zone.importance = parseFloat(input);
      props.setZones(newZones);
      return;
    }

    const newInstants = [...props.data];

    if (props.target.source) { //link
      const instant = newInstants.find(n => n.id === props.target.source.id);
      instant.intervalWidth = instant.intervalWidth ? instant.intervalWidth*(1+input/2) : props.radius/3*(1+input/2); //if intervalWidth defined, multiply. otherwise, use default
    } else { //instant
      const instant = newInstants.find(n => n.id === props.target.id);
      instant.radius = instant.radius ? instant.radius*(1+input/2) : props.radius*(1+input/2); //if radius defined, multiply. otherwise, use default radius
    }
    props.setData(newInstants);

  };

  const handleClose = (e) => {
    props.setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <SelectedDisplay target={props.target}/>
      <label>
        Importance
        <input type="number"
          placeholder="ints relative to 0 (+ and -)"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          required
        />
      </label><br/>
      <input type="submit" value="Submit"/>
    </form>
  )
}

function Connection(props) {
  const handleSubmit = (e) => {
    e.preventDefault();

    /*
      - error handling: check if target[0] and target[1] exists
      - find instant w/ id of target[0] and:
        - If target[1] is:
          - instant, target[1].id
          - interval, {"x": target.x-source.x, "y": mouse pointer, "scale":"smalls"}
          - zone, {"x": 1622, "y": 12, "scale":"smalls"}
    */

    if (!props.target[0] || !props.target[0]) {
      console.log("error")
      return;
    }

    const newInstants = [...props.data];
    const instant = newInstants.find(n => n.id === props.target[0].id);
    if (props.target[1].x) { //for interval and zone
      const target = props.target[1]
      instant.connections = [{"x": target.x, "y": target.y, "scale": target.scale}, 0]
    } else { //for instants
      const instant2 = newInstants.find(n => n.id === props.target[1].id);
      instant.connections = [instant2.id, 0];
      instant2.connections = [instant.id, 1];
    }
    //
    // if (props.target.start) { //zone
    //   const newzones = [...props.zones];
    //   const zone = newzones.find(n => n.id === props.target.id);
    //   zone.importance = parseFloat(input);
    //   props.setZones(newZones);
    //   return;
    // }
    //

    //
    // if (props.target.source) { //link
    //   const instant = newInstants.find(n => n.id === props.target.source.id);
    //   instant.intervalWidth = instant.intervalWidth ? instant.intervalWidth*(1+input/2) : props.radius/3*(1+input/2); //if intervalWidth defined, multiply. otherwise, use default
    // } else { //instant
    //   const instant = newInstants.find(n => n.id === props.target.id);
    //   instant.radius = instant.radius ? instant.radius*(1+input/2) : props.radius*(1+input/2); //if radius defined, multiply. otherwise, use default radius
    // }
    props.setData(newInstants);


  };

  const handleClose = (e) => {
    props.setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <p>
        Connect<br/>
        <b>{props.target[0]?`${props.target[0].name}(${props.target[0].id})`: "None"}</b> {!props.target[2] && "<"}<br/>
        with<br/>
        <b>{props.target[1]?`${props.target[1].name}(${props.target[1].id})`: "None"}</b> {props.target[2]===1 && "<"}
      </p>
      <input type="submit" value="Submit"/>
    </form>
  )
}

