import React, { useState } from 'react';
import './stylesheets/index.scss';
import Data from "./history.json";
import Graphic from "./components/Graphic";
import Image from "./components/Image";
import NewPointForm from "./components/NewPointForm";
// import findMinMax from "./components/findMinMax";



class ToolsBar extends React.Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = {
      toggleLabel: true
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    switch (e.target.id) {
      case 'toggleLabel':
        this.state.toggleLabel ?
          this.setState({toggleLabel: false})
        : this.setState({toggleLabel: true});
      break
      default:
        this.props.setToggle(e.target.id);
    }

    e.preventDefault();
  }

  render() {
    return (
      <nav className="ui relative">
        <ul>
          <li><button id="addPoint" onClick={this.handleClick}>Add Point</button></li>
          <li><button id="toggleLabel" onClick={this.handleClick}>{this.state.toggleLabel ? "Toggle IDs" : "Toggle Labels"}</button></li>
          <li><button id="settings" onClick={this.handleClick}>Settings</button></li>
          <li><button id="import" onClick={this.handleClick}>Import</button></li>
          <li><button id="save" onClick={this.handleClick}>Save & Export</button></li>
        </ul>
      </nav>
    );
  }
}


/*
  - pass object data and mouseclick position to component
  - component is a div set at mouseclick position and displays object data as text
*/
function InfoBox({data}) {
  const target = data.target;
  var content = null;

  switch (data.type) {
    case 'link':
      content =
        <>
          From: {target.source.name} ({target.source.id})<br/>
          To: {target.target.name} ({target.target.id})<br/>
          Color: {target.color ? target.color : "Black"}<br/>
          Width: {target.width ? target.width : "1.67 (default)"}
        </>
    break
    default:
      content =
        <>
          ID: {target.id}<br/>
          Time: {target.x}<br/>
          Y Position: {target.y}<br/>
          Target(s): {target.target ? target.target : "N/A"}<br/>
          Color: {target.color ? target.color : "Black"}<br/>
          Radius: {target.radius ? target.radius : "5 (default)"}
        </>
  };

  return (
    <div className="ui absolute" style={{left: data.position[0] + 'px', top: data.position[1] + 'px'}}>
      {content}
    </div>
  );
}


function App() {

  //user defined
  const radius = 5;

  //for the entire ChronoJSON
  const [src, setSrc] = useState(Data);
  //pertaining to datapoints only
  const [data, setData] = useState(src.nodes[0]); //todo: comparative timelines, layering
  //pertaining to intervals only
  const [intervals, setIntervals] = useState(src.intervals);
  //UI window toggles
  const [toggle, setToggle] = useState();
  //UI info boxes for points, lines, etc.
  const [info, setInfo] = useState();

  /*
    TODO:
    - findMinMax() adjust less frequently. ie: if new data can be panned to, no need to adjust viewport
    - for piecewise timeline, make function that generates different graphics
    - create a master array of all snapshots of data (for scrubber)
  */

  return (
    <>
      <ToolsBar setToggle={setToggle}/>
      {toggle==="addPoint" ? <NewPointForm data={data} setData={setData} setToggle={setToggle}/> : null}
      <div id="main" className="container">
        {src.nodes.length ?
          <Graphic
            data={data}
            setData={setData}
            intervals={intervals}
            setIntervals={setIntervals}
            radius={radius}
            setInfo={setInfo}
          />
          : <div>Upload data or create point below</div>
        }

      </div>
      {info ? <InfoBox data={info}/> : null}
    </>
  );
}

export default App;
