import React, { useState } from 'react';

/*
   UI and function for adding new zones.
*/

function NewZoneForm({zones, setZones, setToggle, scales}) {
  const [label, setLabel] = useState("");
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  const [select, setSelect] = useState("");
  //go thru list of scales in ChronoJSON and create a selection entry for each
  const scalesList = Object.keys(scales).map((key) => {
    return <option value={key}>{key}</option>
  });
  //optional parameters
  const [yStart, setYStart] = useState("");
  const [yEnd, setYEnd] = useState("");
  const [color, setColor] = useState("");
  const [opacity, setOpacity] = useState("");

  // const handleChange = (e) => {
  //   //if it's a checkbox, evaluate if checked. otherwise, evaluate the value
  //   const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
  //   const name = e.target.name;
  //   // console.log(value);
  //   // setLabel(value);
  //   this["set"+name](value);
  //
  //   // setValue({
  //   //   [name]: value
  //   // });
  // }

  const handleSubmit = (e) => {
    //generate new zone
    const zone = {
      "id": zones.length ? zones[zones.length-1].id + 1 : 0,
      "name": label,
      "start": parseFloat(start), //cases where start > end?
      "end": parseFloat(end),
      // "scale": scale, TODO
      ...(yStart && {"yStart": yStart}),
      ...(yEnd && {"yEnd": yEnd}),
      ...(color && {"color": color}),
      ...(opacity && {"opacity": opacity})
    }
    const newZones = [...zones, zone];
    setZones(newZones);
    setToggle(null);
    e.preventDefault();
  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <label>
        Label<span className="red">* </span>
        <input type="text"
          placeholder="any unicode characters"
          value={label}
          onChange={(e) => setLabel(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Time Start<span className="red">* </span>
        <input type="text"
          placeholder="#s, dates, logical operators"
          value={start}
          onChange={(e) => setStart(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Time End<span className="red">* </span>
        <input type="text"
          placeholder="#s, dates, logical operators"
          value={end}
          onChange={(e) => setEnd(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Scale<span className="red">* </span>
        <select name="scales" value={select} onChange={(e) => setSelect(e.target.value)}>
          {scalesList}
        </select>
      </label><br/>
      <label>
        Y Start
        <input
          type="text"
          placeholder="#s, dates, logical operators"
          value={yStart}
          onChange={(e) => setYStart(e.target.value)}
        />
      </label><br/>
      <label>
        Y End
        <input
          type="text"
          placeholder="#s, dates, logical operators"
          value={yEnd}
          onChange={(e) => setYEnd(e.target.value)}
        />
      </label><br/>
      <label>
        Color
        <input
          type="text"
          placeholder="blue, #4DA6FF, etc."
          value={color}
          onChange={(e) => setColor(e.target.value)}
        />
      </label><br/>
      <label>
        Opacity
        <input
          type="number"
          placeholder="0 (transparent) - 1 (opaque)"
          value={opacity}
          onChange={(e) => setOpacity(e.target.value)}
        />
      </label><br/>
      <input type="submit" value="Submit"/>
    </form>
  )
};

export default NewZoneForm;
