import React, { useState } from 'react';

/*
  For saving and downloading JSON data onto computer.
*/

function ExportDataForm({src, data, activeData}) {
  const [fileName, setFileName] = useState("");

  const handleSave = (e) => {
    //syncs changes in nodes data with src
    const newSrc = src;
    newSrc.nodes[activeData] = data;

    //saves synced src as a file
    const fileData = JSON.stringify(newSrc);
    const blob = new Blob([fileData], {type: "text/plain"});
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.download = `${fileName}.json`;
    link.href = url;
    link.click();

    e.preventDefault();
  };

  return (
    <form className="ui absolute center" onSubmit={handleSave}>
      <label>
        File Name <span className="red">* </span>:
        <input
          type="text"
          value={fileName}
          onChange={(e) => setFileName(e.target.value)}
          required
        />
      </label><br/><br/>
      <input type="submit" value="Save Data"/>
    </form>
  );
}

export default ExportDataForm;
