import React, { useState } from 'react';
import "./App.css";
import Data from "./data.json";
import Graphic from "./components/Graphic";
import NewPointForm from "./components/NewPointForm";
import generateLinks from "./components/generateLinks";

//iterates thru xy values in json data to find min and max values
function findMinMax(data) {
  var [minX, minY] = [Infinity, Infinity]; //assign values for multiple vars
  var [maxX, maxY] = [-Infinity, -Infinity];
  var tmpX, tmpY;

  for (var i = data.length-1; i>=0; i--) {
    tmpX = data[i].x;
    tmpY = data[i].y;
    if (tmpX < minX) minX = tmpX;
    if (tmpX > maxX) maxX = tmpX;
    if (tmpY < minY) minY = tmpY;
    if (tmpY > maxY) maxY = tmpY;
  }
  return(
    [minX, maxX, minY, maxY]
  );
}

function App() {

  //user defined
  const radius = 5;
  const fontSize = 20;

  //for the entire ChronoJSON
  const [src, setSrc] = useState(Data);
  //pertaining to datapoints only
  const [data, setData] = useState(src.nodes);

  /*
    TODO:
    - findMinMax() adjust less frequently. ie: if new data can be panned to, no need to adjust viewport
    - for piecewise timeline, make function that generates different graphics
    - create a master array of all snapshots of data (for scrubber)
  */
  // const data = {nodes: src.nodes, links:generateLinks(src.nodes), imgs:src.imgs};

  return (
    <>
      <Graphic data={data} setData={setData} minMax={findMinMax(data)} radius={radius} fontSize={fontSize}/>
      <NewPointForm data={data} setData={setData}/>
      <div className="images">
        {src.imgs ? <Image urls={src.imgs}/> : null}
      </div>
    </>
  );
}

export default App;
