import React from 'react';

/*
   UI for adding new points.
*/

class NewPointForm extends React.Component {
  //controlled component, meaning React is the "single source of truth"
  constructor(props) {
    super(props);
    this.state = {
      label: '',
      xValue: '',
      yValue: '',
      color: '',
      radius: '',
      linked: true,
      endPoint: true,
      targets: '',
      lineColor: '',
      lineWidth: '',
      lineColorA: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    //if it's a checkbox, evaluate if checked. otherwise, evaluate the value
    const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;;
    const name = e.target.name;

    this.setState({
      [name]: value
    });
  }

  handleClose(e) {
    this.props.setToggle(null);
    e.preventDefault();
  }

  handleSubmit(e) { //consider turning into useCallback
    const nodes = this.props.data;

    const tempx = parseFloat(e.target.xValue.value);
    //if y pos specified, convert to float. otherwise, return null
    const tempy = e.target.yValue.value ? parseFloat(e.target.yValue.value) : null;

    //error handling. btw, isNan(null) === false
    if (isNaN(tempx) && isNaN(tempy)) {
      alert("Make sure Time and y Position are numbers!");
    } else if (isNaN(tempx)) {
      alert("Make sure Time is a number!");
    } else if (isNaN(tempy)) {
      alert("Make sure y Position is a number!");
    } else {
      //generate node
      const node = {
        "id": nodes.length ? nodes[nodes.length-1].id + 1 : 0, //id = id of last node + 1. if first node, id = 0
        "name": e.target.label.value,
        "x":tempx,
        "y": e.target.yValue.value ? tempy : nodes[nodes.length-1]? nodes[nodes.length-1].y : 0, // if y not specified, take value of last point
        ...(e.target.color.value && {"color": e.target.color.value}), //if color is specified, create color entry in data
        ...(e.target.radius.value && {"radius": e.target.radius.value}),
        ...(
            !this.state.endPoint ?
            //if not an endpoint, turn IDs into array
            {"target": e.target.targets.value.split(',').map(Number)}
            : {"target": null}
          ),
      };
      const newNodes = () => {
        if (this.state.linked) {
          //if linked, set target of last node to ID of new node
          const newNodesLinked = nodes.map(n =>
            {
              if (n.id === nodes[nodes.length-1].id) {
                return (
                  {
                    ...n,
                    "target":node.id,
                    ...(e.target.lineColor.value && {"lineColor": e.target.lineColor.value}),
                    ...(e.target.lineWidth.value && {"lineWidth": e.target.lineWidth.value})
                  }
                  // e.target.lineColor.value ?
                  //   {...n, "target":nodes.length, "lineColor": e.target.lineColor.value}
                  // : {...n, "target":nodes.length};
                )
              } else { //leave other nodes as is
                return n;
              }
            }
          );
          //return all of that plus new node
          return([...newNodesLinked, node]);
        } else {
          //if not linked, return array with just new node added
          return ([...nodes, node]);
        }
      };
      // const newSrc = Object.assign({}, this.props.src, {nodes: newNodes()});
      this.props.setData(newNodes);
    }
    this.props.setToggle(null);
    e.preventDefault();
  }

  render() {
    return (
      <form className="ui absolute center" onSubmit={this.handleSubmit}>
        <button className="right" onClick={this.handleClose}>x</button><br/>
        <label>
          Label<span className="red">* </span>
          <input type="text"
            name="label"
            placeholder="any unicode characters"
            value={this.state.label}
            onChange={this.handleChange}
            required
          />
        </label><br/>
        <label>
          Time<span className="red">* </span>
          <input
            type="number"
            name="xValue"
            placeholder="numbers only"
            value={this.state.xValue}
            onChange={this.handleChange}
            required
          />
        </label><br/>
        <label>
          y Position
          <input
            type="number"
            name="yValue"
            placeholder="numbers only"
            value={this.state.yValue}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Color
          <input
            type="text"
            name="color"
            placeholder="blue, #4DA6FF, etc."
            value={this.state.color}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Radius
          <input
            type="number"
            name="radius"
            placeholder="5"
            value={this.state.radius}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          Linked to previous?
          <input
            type="checkbox"
            name="linked"
            checked={this.state.linked}
            onChange={this.handleChange}
          />
        </label><br/>
        {this.state.linked &&
          <>
            <label>
              Line Color
              <input
                type="text"
                name="lineColor"
                placeholder="blue, #4DA6FF, etc."
                value={this.state.lineColor}
                onChange={this.handleChange}
              />
            </label><br/>
            <label>
              Line Width
              <input
                type="number"
                name="lineWidth"
                placeholder="1.67"
                value={this.state.lineWidth}
                onChange={this.handleChange}
              />
            </label><br/>
          </>
        }
        <label>
          End point?
          <input
            type="checkbox"
            name="endPoint"
            checked={this.state.endPoint}
            onChange={this.handleChange}
          />
        </label><br/>
        {!this.state.endPoint &&
          <>
            <label>
              Target(s)<span className="red">* </span>
              <input
                type="text"
                name="targets"
                placeholder="point IDs ie: 4, 5"
                value={this.state.targets}
                onChange={this.handleChange}
                required
              />
            </label><br/>
            <label>
              Line Color A
              <input
                type="text"
                name="lineColorA"
                placeholder="blue, #4DA6FF, etc."
                value={this.state.lineColorA}
                onChange={this.handleChange}
              />
            </label><br/>
          </>
        }
        <br/>
        <input type="submit" value="Submit"/>
      </form>
    );
  }
};

export default NewPointForm;
