import React, { useState } from 'react';

/*
   UI and function for adding new zones.
*/

function NewScaleForm({scales, setScales, setToggle}) {
  const [name, setName] = useState("");
  const [min, setMin] = useState("");
  const [max, setMax] = useState("");
  const [units, setUnits] = useState("");
  const [yPos, setYPos] = useState(1);
  //optional parameters
  const [segmentsNum, setSegmentNum] = useState(0);

  const handleSubmit = (e) => {
    console.log(scales)
    //generate new scale
    const scale = {
      "min": parseFloat(min),
      "max": parseFloat(max),
      "units": units, //cases where start > end?
      "yPos": parseFloat(yPos),
      "segments": [] //todo
    }
    const newScales = [...scales, scale];
    setScales(newScales);
    setToggle(null);
    e.preventDefault();
  };

  const handleClose = (e) => {
    setToggle(null);
    e.preventDefault();
  }

  return (
    <form className="ui absolute center" onSubmit={handleSubmit}>
      <button className="right" onClick={handleClose}>x</button><br/>
      <label>
        Name<span className="red">* </span>
        <input type="text"
          placeholder="any unicode characters"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Minimum Value<span className="red">* </span>
        <input type="text"
          placeholder="#s for now"
          value={min}
          onChange={(e) => setMin(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Maximum Value<span className="red">* </span>
        <input type="text"
          placeholder="#s for now"
          value={max}
          onChange={(e) => setMax(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Units<span className="red">* </span>
        <input type="text"
          placeholder="ie: Unicorns per 100K"
          value={units}
          onChange={(e) => setUnits(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Vertical Position<span className="red">* </span>
        <input
          type="text"
          placeholder="0 (top) - 1 (bottom)"
          value={yPos}
          onChange={(e) => setYPos(e.target.value)}
          required
        />
      </label><br/>
      <label>
        Segments
        <input
          type="number"
          placeholder="granularities within scale"
          value={segmentsNum}
          onChange={(e) => setSegmentNum(e.target.value)}
        />
      </label><br/>
      <input type="submit" value="Submit"/>
    </form>
  )
};

export default NewScaleForm;
