import React, { useState } from 'react';
import "./App.css";
import Data from "./data.json";
import Graphic from "./components/Graphic"

class NewPointForm extends React.Component {
  //controlled component, meaning React is the "single source of truth"
  constructor(props) {
    super(props);
    this.state = {
      label: '',
      xValue: '',
      yValue: '',
      color: '',
      endPoint: false,
      targets: '',
      lineColor: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    //if it's a checkbox, evaluate if checked. otherwise, evaluate the value
    const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;;
    const name = e.target.name;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(e) {
    const nodes = this.props.nodes;

    const tempx = parseFloat(e.target.xValue.value);
    //if y pos specified, convert to float. otherwise, return null
    const tempy = e.target.yValue.value ? parseFloat(e.target.yValue.value) : null;
    // console.log(nodes[nodes.length-1].y);

    //error handling. btw, isNan(null) === false
    if (isNaN(tempx) && isNaN(tempy)) {
      alert("Make sure Time and y Position are numbers!");
    } else if (isNaN(tempx)) {
      alert("Make sure Time is a number!");
    } else if (isNaN(tempy)) {
      alert("Make sure y Position is a number!");
    } else {
      //send to server
      const node = {
        "id": nodes.length, //todo
        "name": e.target.label.value,
        "x":tempx,
        "y": e.target.yValue.value ? tempy : nodes[nodes.length-1].y,
        ...(e.target.color.value && {"color": e.target.color.value}),
        ...(
            !this.state.endPoint ?
            //if not an endpoint AND has specified IDs, turn IDs into array
            e.target.targets.value ? {"target": e.target.targets.value.split(',').map(Number)} : null //otherwise, return null
            : {"target": null} //if endpoint, return target: null
          ),
        ...((!this.state.endPoint && e.target.lineColor.value) && {"lineColor": e.target.lineColor.value})
      };
      const newNodes = [...nodes, node];
      console.log(node);
    }
    e.preventDefault();
  }

  render() {
    return (
      <form className="ui" onSubmit={this.handleSubmit}>
        <label>
          Label<span className="red">* </span>
          <input type="text"
            name="label"
            placeholder="any unicode characters"
            value={this.state.label}
            onChange={this.handleChange}

          />
        </label><br/>
        <label>
          Time<span className="red">* </span>
          <input
            type="text"
            name="xValue"
            placeholder="numbers only"
            value={this.state.xValue}
            onChange={this.handleChange}

          />
        </label><br/>
        <label>
          y Position<span className="red">* </span>
          <input
            type="text"
            name="yValue"
            placeholder="numbers only"
            value={this.state.yValue}
            onChange={this.handleChange}

          />
        </label><br/>
        <label>
          Color
          <input
            type="text"
            name="color"
            placeholder="blue, #4DA6FF, etc."
            value={this.state.color}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          End point?
          <input
            type="checkbox"
            name="endPoint"
            checked={this.state.endPoint}
            onChange={this.handleChange}
          />
        </label><br/>
        {!this.state.endPoint &&
          <>
            <label>
              Target(s)
              <input
                type="text"
                name="targets"
                placeholder="point IDs ie: 4, 5"
                value={this.state.targets}
                onChange={this.handleChange}
              />
            </label><br/>
            <label>
              Line Color
              <input
                type="text"
                name="lineColor"
                placeholder="blue, #4DA6FF, etc."
                value={this.state.lineColor}
                onChange={this.handleChange}
              />
            </label><br/>
          </>
        }
        <br/>
        <input type="submit" value="Submit"/>
      </form>
    );
  }
}

//iterates thru xy values in json data to find min and max values
function findMinMax(data) {
  var [minX, minY] = [Infinity, Infinity]; //assign values for multiple vars
  var [maxX, maxY] = [-Infinity, -Infinity];
  var tmpX, tmpY;

  for (var i = data.length-1; i>=0; i--) {
    tmpX = data[i].x;
    tmpY = data[i].y;
    if (tmpX < minX) minX = tmpX;
    if (tmpX > maxX) maxX = tmpX;
    if (tmpY < minY) minY = tmpY;
    if (tmpY > maxY) maxY = tmpY;
  }
  return(
    [minX, maxX, minY, maxY]
  );
}

/*
   process raw data in function that extract following arrays:
   0: of all nodes excluding loose points
   1: from 1, all links between nodes, {source, target, color}

   Graphic component will be passed these for use in this way:
   - [1] for drawing linkHorizontal
   - Data.nodes for drawing cirlces and labels
*/
function generateLinks(data) {
  // const nodes = []; //array of all nodes that are NOT detached
  const links = []; //array of all node links

  /// IMPORTANT: all ending nodes must be marked w target:null ///

  data.forEach((item, i) => {
    if (item.target !== null) { //detached points are defined w target: null as opposed to being undefined
      // nodes.push(item);
      if (item.target) { //for nodes w multiple targets, create link for each target
        item.target.forEach((n) => {
          links.push({source:item, target:data[n], color:item.lineColor});
        });
      } else {
        links.push({source:item, target:data[i+1], color:item.lineColor})
      };
    }
  });

  return links;
};

function App() {

  //user defined
  const radius = 5;
  const fontSize = 20;

  //data is initially set to imported json array Data
  var [src, setSrc] = useState(Data);
  // console.log(generateLinks(src.nodes));
  const data = {nodes: src.nodes, links:generateLinks(src.nodes), imgs:src.imgs};

  // console.log(findMinMax(Data.line1));
  return (
    <>
      <Graphic src={data} minMax = {findMinMax(src.nodes)} radius={radius} fontSize={fontSize}/>
      <NewPointForm nodes={src.nodes}/>
    </>
  );
}

export default App;
