// import DeletePoint from "./DeletePoint";
/*
  - pass object info and mouseclick position to component
  - component is a div set at mouseclick position and displays object info as text
*/

function InfoBox({info, setInfo, data, setData}) {
  const target = info.target;
  var content = null;

  /*
    deleting point i:
    - find data[i-1] and set its target to data[i+1]. then remove data[i] from array
    - if data[i] is first point, just remove from array

    - if first point => just delete
    - prevNode exists but is not linked => just delete
    - prevNode exists and is linked, but no nextNode => set prevNode target to null, then delete
    - prevNode exists, is linked, and has next node => set prevNode target to nextNode, then delete
    - prevNode exists, is linked to multiple nodes => remove target node from prevNode, then delete
  */
  const handleDeletePoint = (e) => {
    e.preventDefault();

    const newData = {...data};
    const dataKeys = Object.entries(newData).map(([k,v]) => k);
    const dataValues = Object.entries(newData).map(([k,v]) => v);
    // console.log(dataValues[0]);
    // const node = newData[target[0]];
    // const prevNode = newData[target[0]-1];
    // const nextNode = newData[target[0]+1];


    if (target[0] === dataKeys[0]) {  //if this is the fist node, delete point and return data
      delete newData[target[0]];
    } else {
      dataValues.forEach(v => {
        if (v.target && v.target.includes(target[0])) { //for each data entry v, does v.target include id of deleted point?
          console.log(v)
          v.target.filter((x) => x !== target[0]);
        }
        // if (v.target===[]) {
        //   v.target = null;
        // }

      // if (Array.isArray(v.target) && v.target.includes(target[0])) { //for each data entry v, does v.target include id of deleted point?
      //       if (v.target.length > 1) { //if multiple targets, delete only point
      //         v.target.filter((x) => x !== target[0]);
      //       } else if (nextNode) { //if there's a next node, set it as the new target then delete point
      //         v.target
      //         return ({...v, "target": [target.id+1]});
      //       } else { //if there are no more nodes after this, set target to null
      //         return ({...v, "target": null});
      //       }
      //     } else {
      //       return v;
      //     }
    });
    console.log(newData);
      // const updatedData = data.map(n =>
      //   {
      //     if (Array.isArray(n.target) && n.target.includes(target.id)) { //for each data entry n, does n.target include id of deleted point?
      //       if (n.target.length > 1) { //if multiple targets, delete only point
      //         return ({...n, "target": n.target.filter((x) => x !== target.id)});
      //       } else if (nextNode) { //if there's a next node, set it as the new target then delete point
      //         return ({...n, "target": [target.id+1]});
      //       } else { //if there are no more nodes after this, set target to null
      //         return ({...n, "target": null});
      //       }
      //     } else {
      //       return n;
      //     }
      //   }
      // )
      // return updatedData.filter(n => n !== target);
    }/*


    setData(newData);
    setInfo(null);*/
  }

  switch (info.type) {
    case 'link':
      content =
        <>
          From: {target.source.name} ({target.source.id})<br/>
          To: {target.target.name} ({target.target.id})<br/>
          Color: {target.color ? target.color : "Black"}<br/>
          Width: {target.width ? target.width : "1.67 (default)"}<br/>
          Opacity: {target.opacity ? target.opacity : "1"}<br/>
          Dashed: {target.dashed ? target.dashed : "N/A"}
        </>
    break
    case 'zone':
      content =
        <>
          ID: {target.id}<br/>
          Label: {target.name}<br/>
          Time Start: {target.start}<br/>
          Time End: {target.end}<br/>
          Y Start: {target.yStart ? target.yStart : "N/A"}<br/>
          Y End: {target.yEnd ? target.yEnd : "N/A"}<br/>
          Color: {target.color ? target.color : "#999 (default)"}<br/>
          Opacity: {target.opacity ? target.opacity : "0.5"}<br/>
        </>
    break
    default:
      content =
        <>
          ID: {target[0]}<br/>
          Time: {target[1].x}<br/>
          Y Position: {target[1].y}<br/>
          Target(s): {target[1].target ? target[1].target : "N/A"}<br/>
          Color: {target[1].color ? target[1].color : "Black"}<br/>
          Opacity: {target[1].opacity ? target[1].opacity : "1"}<br/>
          Radius: {target[1].radius ? target[1].radius : "5 (default)"}<br/>
          <button onClick={handleDeletePoint}>Delete Point</button>
        </>
  };

  return (
    <div className="ui absolute" style={{left: info.position[0] + 'px', top: info.position[1] + 'px'}}>
      {content}
    </div>
  );
}

export default InfoBox;
