import React, { useState } from 'react';
import "./App.css";
import Data from "./data.json";
import Graphic from "./components/Graphic"

class NewPointForm extends React.Component {
  //controlled component, meaning React is the "single source of truth"
  constructor(props) {
    super(props);
    this.state = {
      label: '',
      xValue: '',
      yValue: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(event) {
    console.log(event.target.label.value);
    // alert(`label: ${event.target.label}, x: ${event.target.xValue}, y: ${event.target.yValue}`);
    event.preventDefault();
  }

  render() {
    return (
      <form className="ui" onSubmit={this.handleSubmit}>
        <label>
          Label:
          <input type="text"
            name="label"
            value={this.state.label}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          x Value:
          <input
            type="text"
            name="xValue"
            value={this.state.xValue}
            onChange={this.handleChange}
          />
        </label><br/>
        <label>
          y Value:
          <input
            type="text"
            name="yValue"
            value={this.state.yValue}
            onChange={this.handleChange}
          />
        </label><br/><br/>
        <input type="submit" value="Submit"/>
      </form>
    );
  }
}

function findMinMax(props) {
  var [minX, minY] = [Infinity, Infinity]; //assign values for multiple vars
  var [maxX, maxY] = [-Infinity, -Infinity];
  var tmpX, tmpY;

  for (var i = props.length-1; i>=0; i--) {
    tmpX = props[i].x;
    tmpY = props[i].y;
    if (tmpX < minX) minX = tmpX;
    if (tmpX > maxX) maxX = tmpX;
    if (tmpY < minY) minY = tmpY;
    if (tmpY > maxY) maxY = tmpY;
  }
  return(
    [minX, maxX, minY, maxY]
  );
}

function App() {

  //user defined
  const radius = 5;
  const fontSize = 20;

  //data is initially set to imported json array Data
  var [src, setSrc] = useState(Data.line1);
  // console.log(findMinMax(Data.line1));
  return (
    <>
      <Graphic src={src} minMax = {findMinMax(src)} radius={radius} fontSize={fontSize}/>
      <NewPointForm />
    </>
  );
}

export default App;
