import React, { useState } from 'react';
import './stylesheets/index.scss';
import Data from "./data.json";
import Graphic from "./components/Graphic";
import Image from "./components/Image";
import NewInstantForm from "./components/NewInstantForm";
import NewIntervalForm from "./components/NewIntervalForm";
import NewZoneForm from "./components/NewZoneForm";
import NewScaleForm from "./components/NewScaleForm";
import EditScaleForm from "./components/EditScaleForm";
import InfoBox from "./components/InfoBox";
import ExportDataForm from "./components/ExportDataForm";
import ImportDataForm from "./components/ImportDataForm";
import NavBar from "./components/NavBar";
import Layers from "./components/Layers";
import Scrubber from "./components/Scrubber";
import Legend from "./components/Legend";
import Certainty from "./components/Certainty";
import Importance from "./components/Importance";
import Connection from "./components/Connection";
import WelcomeMessage from "./components/WelcomeMessage";
import generateIntervals from "./components/generateIntervals";

function EmptyProj({setToggle}) {
  return (
    <div className="flexCenter">
      <button className="uiButton" onClick={() => setToggle("import")}>Upload data</button> or <button className="uiButton" onClick={() => setToggle("addScale")}>Create a scale</button>
    </div>
  );
}



function ScalesList(scales) {
  Object.keys(scales).map((key, i) => {
    return <option value={key} key={i}>{key}</option>
  });
}

function App() {
  //blank ChronoJSON template
  const nullData = {
    "imgs": [],
    "instants":
    [
      {}
    ],
    "zones":
    [],
    "scales":
    {}
  };

  const scaleData = {
    "imgs": [],
    "instants":
    [
      []
    ],
    "zones":
    [],
    "scales":
    {
      "Egyptian Dynasties": {
        "min": -3100,
        "max": -30,
        "units": "Years BCE",
        "yPos": 1,
        "tickValues": [],
        "segments": [
          {"max": -2686, "label": "Early Dynastic"},
          {"min": -2686, "max": -2181, "label": "Old Kingdom"},
          {"min": -2181, "max": -2040, "label": "First Intermediate Period"},
          {"min": -2040, "max": -1649, "label": "Middle Kingdom"},
          {"min": -1649, "max": -1550, "label": "Second Intermediate Period"},
          {"min": -1550, "max": -1077, "label": "New Kingdom"},
          {"min": -1077, "max": -656, "label": "Third Intermediate Period"},
          {"min": -656, "max": -332, "label": "Late Period"},
          {"min": -332, "label": "Graeco-Roman"}
        ]
      }
    }
  };


  //user defined
  const radius = 5;
  const date = new Date();
  const [fileName, setFileName] = useState("");

  //for the entire ChronoJSON
  const [src, setSrc] = useState(nullData);

  //for ChronoJSON history. for undo and redo
  const [history, setHistory] = useState([])

  //for toggling the active data as used by scrubber
  const [activeData, setActiveData] = useState(0);
  //for toggling the active layer
  const [activeLayer, setActiveLayer] = useState();

  //initialize datapoints
  const [data, setData] = useState(src.instants[activeData]);
  //links are initialized in Graphic.js
  // const [links, setLinks] = useState(generateLinks(src.instants[activeData]))
  //initialize zones
  const [zones, setZones] = useState(src.zones);
  //initialize scales
  const [scales, setScales] = useState(src.scales);

  //toggles datapoint IDs vs labels
  const [showLabel, setShowLabel] = useState(true);

  //for toggling the active scale
  const [activeScale, setActiveScale] = useState(Object.keys(scales)[0])

  //UI window toggles
  const [toggle, setToggle] = useState();
  //UI info boxes for points, intervals, etc.
  const [info, setInfo] = useState();
  //Inflection info
  const [inflectTarget, setInflectTarget] = useState();
  //Syntactic inflections order
  const [syntacticOrder, setSyntacticOrder] = useState([null, null, null]); //[target1, target2, #]. # dictates order, switches b/w 0 and 1

  //functions
  const updateSrc = (newData, key) => {
    setHistory([...history, src]) //history.length-1 is last src before this
    const newSrc = src;
    //why is console.log(data) before & after the same, but new data point still shows up
    if (key==="zones") {
      setZones(newData);
      newSrc.zones = zones;
    } else {
      setData(newData);
      newSrc.instants[activeData] = data;
    }
    setSrc(newSrc);
    setToggle(null);
  }

  // console.log(info);
  /*
    TODO:
    - for piecewise timeline, make function that generates different graphics
  */

  return (
    <>
      {info && <InfoBox
        info={info}
        setInfo={setInfo}
        history={history} setHistory={setHistory}
        data={data} setData={setData}
        src={src} setSrc={setSrc}
        updateSrc={updateSrc}
        zones={zones}
      />}
      <NavBar
        toggle={toggle}
        setToggle={setToggle}
        data={data} showLabel={showLabel}
        setShowLabel={setShowLabel}
        hasScales={Object.keys(scales).length}
        fileName={fileName}
        setFileName={setFileName}
      />
      {toggle==="scrubber" && <Scrubber activeData={activeData} setActiveData={setActiveData} src={src} setData={setData}/>}
      {toggle==="NewInstantForm" &&
        <NewInstantForm
          data={data}
          setData={setData}
          target={inflectTarget}
          setToggle={setToggle}
          scales={scales}
          activeScale={activeScale}
          activeData={activeData}
          history={history}
          setHistory={setHistory}
          updateSrc={updateSrc}
        />
      }
      {toggle==="editScale" && <EditScaleForm scales={scales} setScales={setScales} setToggle={setToggle} activeScale={activeScale} setActiveScale={setActiveScale}/>}
      {toggle==="addZone" && <NewZoneForm zones={zones} setZones={setZones} setToggle={setToggle} scales={scales} activeScale={activeScale}/>}
      {toggle==="addInterval" && <NewIntervalForm data={data} setData={setData} setToggle={setToggle} scales={scales} activeScale={activeScale} history={history} setHistory={setHistory}/>}
      {toggle==="addScale" && <NewScaleForm scales={scales} setScales={setScales} setToggle={setToggle} setActiveScale={setActiveScale}/>}
      {toggle==="save" &&
        <ExportDataForm
          src={src}
          setSrc={setSrc}
          data={data}
          zones={zones}
          scales={scales}
          activeData={activeData}
          setToggle={setToggle}
          fileName={fileName}
        />
      }
      {toggle==="legend" && <Legend/>}
      {toggle==="layers" && <Layers activeLayer={activeLayer} setActiveLayer={setActiveLayer} data={data}/>}
      {toggle==="import" &&
        <ImportDataForm
          setSrc={setSrc}
          setScales={setScales}
          setZones={setZones}
          setData={setData}
          activeData={activeData}
          setToggle={setToggle}
          setFileName={setFileName}
        />
      }
      {toggle==="certainty" &&
        <Certainty
          target={inflectTarget}
          setToggle={setToggle}
          data={data}
          setData={setData}
          zones={zones}
          setZones={setZones}
        />
      }
      {toggle==="importance" &&
        <Importance
          target={inflectTarget}
          setToggle={setToggle}
          data={data}
          setData={setData}
          zones={zones}
          setZones={setZones}
          radius={radius}
        />
      }
      {toggle==="connection" &&
        <Connection
          target={syntacticOrder}
          setToggle={setToggle}
          data={data}
          setData={setData}
          zones={zones}
          setZones={setZones}
        />
      }

      {fileName ?
        <div id="main" className="container fullHeight">
          {Object.keys(scales).length ?
            <Graphic
              data={data}
              setData={setData}
              zones={zones}
              setZones={setZones}
              radius={radius}
              info={info}
              setInfo={setInfo}
              setInflectTarget={setInflectTarget}
              showLabel={showLabel}
              activeLayer={activeLayer}
              toggle={toggle}
              setToggle={setToggle}
              scales={scales}
              setScales={setScales}
              syntacticOrder={syntacticOrder}
              setSyntacticOrder={setSyntacticOrder}
              activeScale={activeScale}
            />
            : <EmptyProj setToggle={setToggle}/>
          }
          {src.imgs.length>0 && <Image urls={src.imgs}/>}
        </div> :
        <WelcomeMessage setFileName={setFileName}/>
      }
    </>
  );
}

export default App;
